/* HEADER */
:root{
     --header-title: "AREA-21";
     --header-subtitle: "A MAGICA AD MUNDANUM";
}
 div#extra-div-1{
     background-image: url(http://scp-sandbox-3.wdfiles.com/local--files/12-aromantic-abatement/21Head.png);
}
 .top-left-box > .item{
     display: inline;
}
.Ngo {
    padding: 4px 16px 4px 16px;
    box-shadow: 0px 0px 3px rgba(0, 0, 0, 0.3);
    width: 75%;
    margin: auto;
    margin-bottom: 28px;
    margin-top: 28px;
    background: #FBF9F3;
    border: dashed 0.1rem #5D5D5D;
}
.muddle {
    padding: 4px 16px 4px 16px;
    box-shadow: 0px 0px 3px rgba(0, 0, 0, 0.3);
    width: 85%;
    margin: auto;
    margin-bottom: 28px;
    margin-top: 28px;
    background: #E9FBDF;
    border: solid 2px #5D5D5D;
}
.backspace {
    border-left: solid 3px rgb(43, 95, 179);
    border-right: solid 3px rgb(43, 95, 179);
    padding: 25px 25px 25px 25px;
    box-shadow: 0px 0px 3px rgba(0, 0, 0, 0.3);
    margin: auto;
    background: #f7f7f7;
}
.acroabate {
    background: #D0F9F9;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.3);
    margin: 26px auto 0;
    max-width: 550px;
    padding: 24px;
    margin-bottom: 36px;
    position: relative;
    width: 80%;
}
.acroabate:before,
.acroabate:after {
    content: "";
    height: 98%;
    position: absolute;
    width: 100%;
    z-index: -1;
}
.acroabate:before {
    background: #B4E8E8;
    box-shadow: 0 0 8px rgba(0, 0, 0, 0.2);
    left: -5px;
    top: 4px;
    transform: rotate(-2.5deg);
}
.acroabate:after {
    background: #9CD5D5;
    box-shadow: 0 0 3px rgba(0, 0, 0, 0.2);
    right: -3px;
    top: 1px;
    transform: rotate(1.4deg);
}
.notation {
    border-left: solid 3px rgb(0, 128, 128);
    border-right: solid 3px rgb(0, 128, 128);
    padding: 25px 25px 25px 25px;
    box-shadow: 0px 0px 3px rgba(0, 0, 0, 0.3);
    margin: auto;
    background: #f7f7f7;
}
.Nascimbeni {
    border-left: solid 3px rgb(204, 153, 0);
    border-right: solid 3px rgb(204, 153, 0);
    padding: 25px 25px 25px 25px;
    box-shadow: 0px 0px 3px rgba(0, 0, 0, 0.3);
    margin: auto;
    background: #f7f7f7;
}
