content:
[[module css]]
.wrap {
	perspective: 800px;
	perspective-origin: 50% 50%;
}
.cube {
	position: relative;
	width: 100px;
	transform-style: preserve-3d;
  margin: auto;
  padding-bottom: 200px;
}
.cube div{
	position: absolute;
	width: 100px;
	height: 100px;
}
.back {
	transform: translateZ(-50px) rotateY(180deg);
  background: url(http://scp-jp-sandbox2.wdfiles.com/local--files/guideline%3A1600con17/anomalous.png);
  background-size: 100% 100%;
}
.mright {
	transform: rotateY(-270deg) translateX(50px);
	transform-origin: top right;
  background: url(http://scp-jp-sandbox2.wdfiles.com/local--files/guideline%3A1600con17/russia.png);
  background-size: 100% 100%;

}
.left {
	transform: rotateY(270deg) translateX(-50px);
	transform-origin: center left;
  background: url(http://scp-jp-sandbox2.wdfiles.com/local--files/guideline%3A1600con17/object.png);
  background-size: 100% 100%;

}
.top {
	transform: rotateX(-90deg) translateY(-50px);
	transform-origin: top center;
  background: url(http://scp-jp-sandbox2.wdfiles.com/local--files/guideline%3A1600con17/bleach.png);
  background-size: 100% 100%;

}
.bottom {
	transform: rotateX(90deg) translateY(50px);
	transform-origin: bottom center;
  background: url(http://scp-jp-sandbox2.wdfiles.com/local--files/guideline%3A1600con17/horror.png);
  background-size: 100% 100%;

}
.front {
	transform: translateZ(50px);
  background: url(http://scp-jp-sandbox2.wdfiles.com/local--files/guideline%3A1600con17/win.png);
  background-size: 100% 100%;

}
@keyframes spin {
  0%{
    transform: rotateY(360deg) rotateX(360deg) rotateZ(360deg);
  }
  20% {
        transform: rotateY(360deg) rotateX(180deg) rotateZ(360deg);

  }
  100% {
    transform: rotateY(-360deg) rotateX(-360deg) rotateZ(-360deg);
  }
}

.cube {
	animation: spin 10s infinite linear;
}
[[/module]]

