  <script src="//scpsandbox2.wikidot.com/local--files/anaxagoras/jquery.js"></script>

<style>
td, tr, table{
    border-collapse: collapse;
}

.jokemessage{
    text-align: center;
    color: white;
    height: 30px;
    width: 30px;
    border: 1px white solid;
    background-color: black;
}
</style>

<div id="joke_table"></div>

<script>
var message = "DATAEXPUNGEDREDACTEDYDOWEHAVREALLYFUCKINGOPCOGNITOHAZARDSNEXTONEWILLPROBAMNESIAMYBRAINLIFESOKYPOKEIT";

var table = $("<table></table>");
for(i=0; i<10; i++){
    var row = $("<tr></tr>");
    for(j=0; j<10; j++){
        var column = $("<td></td>");
        column.text(message.charAt(i*10 + j));
        column.addClass("jokemessage");
        row.append(column);
    }
    table.append(row);
}

$('#joke_table').append(table);
</script>

