[[module ThemePreviewer noUi="true" theme_url="https://cdn.jsdelivr.net/gh/scp-cn-tech/sigma9@cn/cn/sigma9_ch.min.css"]]
[[module css]]
/*页面布局-基础 开始 ↓*/
body, #container-wrap-wrap, #container-wrap, #content-wrap, #page-content {
    min-height: 99.9vh !important;
    max-height: 99.9vh !important;
}

body {
    background: #F7F7F7;
    color: #333;
    font-size: 0.9rem;
}

#page-info {
    display:none !important;
}

.page-watch-options {
    display:none !important;
}

#footer {
    display:none !important;
}

.license-area {
    display:none;
}

#footer-bar {
    display: none;
}

#breadcrumbs {
    display: none !important;
}

#header {
    display: none;
}

div#container-wrap {
    background: none;
}

.NOpaddingP p {
	display: block;
	margin-block-start: 0em;
	margin-block-end: 0em;
	margin-inline-start: 0px;
	margin-inline-end: 0px;
}

#content-wrap {
    margin: 0 auto;
    max-width: -webkit-fill-available;
    min-height: 0;
}

#side-bar {
    display: none;
}

#main-content {
    max-width: 100%;
    margin: 0 auto;
    padding: 0em;
}

#page-title {
    display: none;
}

strong {
    color: #fff;
}

.owindow {
    background-color: #282828;
    width: 50em;
    border: 2px solid #BBB;
    text-align: left;
    position: absolute;
}

.owindow .title {
    cursor: move;
    font-size: 120%;
    font-weight: bold;
    padding: 0.2em 1em;
    background-color: #464646;
    height: 1%;
}

.owindow .button-bar a {
    margin: 0.2em 0.2em;
    padding: 0.1em 1em;
    text-decoration: none;
    background-color: #464646;
    border: 1px solid #AAA;
}

.owindow .button-bar a:hover {
    background-color: #0c0c0c;
}

#main-content .page-tags {
    margin: 1em 0 0;
    padding: 0;
    display: none;
}

h1, h2, h3, h4, h5, h6 {
    margin: 0.5em 0 0.4em;
    padding: 0;
    letter-spacing: 1px;
    color: #fff;
}

.titlePage{
    height: 3vh;
}

blockquote, div.blockquote {
    background-color: #303034;
    box-shadow: 0.05rem 0.1rem 0.3rem rgb(0 0 0 / 25%);
    border: 1px solid #9990;
}

.code {
    background-color: #303034;
    border: none;
}

.viewpage .list-pages-box {
    display: flex;
    flex-wrap: wrap;
    align-items: center;
    justify-content: center;
}

.viewpage2 .list-pages-box {
    display: flex;
    flex-wrap: wrap;
    flex-direction: column;
    align-items: center;
    justify-content: center;
}
/*页面布局-基础 结束 ↑*/
/*编辑 开始 ↓*/
.page-options-bottom {
    display: none;
}
/*编辑 结束 ↑*/
/*资讯布局 开始 ↓*/
.PersonAll {
    display: grid;
    align-items: center;
    grid-template-columns: auto 1fr;
    grid-template-rows: auto;
    grid-template-areas:
     "PersonLogo PersonIntroduce";
}

.PersonLogo {
    grid-area: PersonLogo;
}

.PersonIntroduce {
    grid-area: PersonIntroduce;
    text-align: left;
}
/*资讯布局 结束 ↑*/
/*TopBar布局 开始 ↓*/

.TopBar {
    grid-area: TopBar;
    display: grid;
    align-items: center;
    grid-template-columns: auto;
    grid-template-rows: auto;
    grid-template-areas:
     "TopBarA NoUseBlock MainPerson";
}

.TopBarA {
    grid-area: TopBarA;
    display: grid;
    align-items: center;
    grid-template-columns: auto;
    grid-template-rows: auto;
    grid-template-areas:
     "MainPage TrendsPage CollectionPage CompilationPage";
}

.MainPage {
    grid-area: MainPage;
}

.TrendsPage {
    grid-area: TrendsPage;
}

.CollectionPage {
    grid-area: CollectionPage;
}

.CompilationPage {
    grid-area: CompilationPage;
}

.NoUseBlock {
    grid-area: NoUseBlock;
}

.MainPerson {
    grid-area: MainPerson;
    display: grid;
    align-items: center;
    grid-template-columns: auto;
    grid-template-rows: auto;
    grid-template-areas:
     "Followers Manuscript Comment Assist Column";
}
/*TopBar布局 结束 ↑*/
/*MainPageContentAll布局 开始 ↓*/
/*
.MainPageContentAll {
    grid-area: MainPageContentAll;
    display: grid;
    align-items: center;
    grid-template-columns: auto;
    grid-template-rows: auto;
    grid-template-areas:
     "MainPageContent Authentication";
}

.Authentication {
    grid-area: Authentication;
}
*/

.MainPageContent {
    grid-area: MainPageContent;
    display: grid;
    align-items: center;
    grid-template-columns: auto;
    grid-template-rows: auto;
    grid-template-areas:
     "MainPageA MainPageB MainPageC MainPageD NoUseBlock";
}

.MainPageA {
    grid-area: MainPageA;
}

.MainPageB {
    grid-area: MainPageB;
}

.MainPageC {
    grid-area: MainPageC;
}

.MainPageD {
    grid-area: MainPageD;
}
/*TopBar布局 结束 ↑*/
[[/module]]

