[[module css]]

div#container-wrap {
    background: url(http://cicnsandbox.wikidot.com/local--files/component:destruction-sequence-agreement-css/DSAcss) top left repeat-x;
    background-size: 1182px;
}

body{
    background:url(http://cicnsandbox.wikidot.com/local--files/component:destruction-sequence-agreement-css/body-url);
}

#header h1 a {
    padding: 113px 0 25px;
}

#header {
    height: 190px;
    position: relative;
    z-index: 10;
    padding-bottom: 22px;
    background: url(http://scpsandboxcn.wdfiles.com/local--files/ce-e/new-alpha.png) top center no-repeat;
    background-size: 100px 100px;
}

div#header h1 a:before {
    content: "ALPHA";
    color: rgb(255, 255, 255);
    font-size: 50px;
}

div#header h2 ::before {
    content: "研究，开拓，整合";
    color: rgb(138, 255, 223);
    font-size: 20px;
}

#header h1 a {
    font-size: 0%;
    font-family: auto;
}

#header h1, #header h2 {
    margin-left: 0;
    float: none;
    text-align: center;
}

#header h2 {
    padding: 47px;
}

#header h2 span {
    font-size: 0%;
}

/*分割*/

#top-bar {
    position: absolute;
    top: 184px;
    right: auto;
    width: 100%;
    height: 25px;
    line-height: 18px;
    padding: 0;
    margin: 0 auto;
    z-index: 20;
    font-size: 100%;
    justify-content: center;
    display: flex;
}

#search-top-box {
    position: absolute;
    top: 40px;
    right: 9px;
    width: 250px;
    text-align: right;
}

#content-wrap {
    max-width: 750px;
}

#main-content {
    max-width: 100% !important;
}
/*分割*/

#page-title {
    border: none;
    text-align: center;
}

#page-title {
    color: #005d63;
}

a:visited {
    color: #0006ff;
}

.scp-image-block img {
    border: 0;
    width: 300px;
    background: #424242;
}

#content-wrap h1 {
    color: #00848c;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    font-family: 'Noto Sans SC', sans-serif;
}

#content-wrap h1::after {
    content: "";
    -webkit-box-flex: 1;
    -ms-flex-positive: 1;
    flex-grow: 1;
    height: 2px;
    margin: auto auto auto 0.5em;
    background-color: #00676b;
}

a {
    color: #0087ff;
}



/* TABVIEW */
 .yui-navset .yui-nav, .yui-navset .yui-navset-top .yui-nav {
     border: none;
}
 .yui-navset .yui-nav a, .yui-navset .yui-navset-top .yui-nav a {
     border: none;
}
 .yui-navset .yui-nav a em {
     background-color: #e4e4e4;
     border: none;
}
 .yui-navset .yui-nav .selected a, .yui-navset .yui-nav .selected a:focus, .yui-navset .yui-nav .selected a:hover {
     background-image: none;
     border: none;
}
 .yui-navset .yui-nav .selected a, .yui-navset .yui-nav .selected a em {
     border-color: transparent;
     background-color: #E72A32;
     border: none;
}
 .yui-navset .yui-content {
     background-color: #fbfbfb;
    /* content background color */
     padding: 1rem;
}
 div.prompt .yui-content {
     color: #141414;
}
 .yui-navset .yui-content, .yui-navset .yui-navset-top .yui-content {
     border: none;
     border-left: 3px solid #efefef;
}
 .yui-navset-left .yui-content {
     border-left-color: #b7a9a9;
}

.yui-navset .yui-nav .selected a, .yui-navset .yui-nav .selected a em {
    border-color: transparent;
    box-shadow: 0px 1px 2px rgba(117, 117, 117, 0.44);
    background-color: #4e4e4e;
}

.yui-navset .yui-nav li a, .yui-navset-top .yui-nav li a, .yui-navset-bottom .yui-nav li a {
    width: 10rem;
    max-width:45.8rem
}

.yui-navset .yui-content {
    background-color:transparent;
}

.yui-navset .yui-content, .yui-navset .yui-navset-top .yui-content {
    background-color:transparent;
}

.yui-navset .yui-nav, .yui-navset .yui-navset-top .yui-nav {
    border-color: transparent
}
.yui-navset .yui-nav, .yui-navset .yui-navset-top .yui-nav {
    border: solid transparent;
    border-width: 0 0 5px;
    position: relative;
    zoom: 1;
    text-align: center;
}

.yui-navset .yui-content, .yui-navset .yui-navset-top .yui-content {
    border: 1px solid transparent;
    border-top-color: transparent;
    padding: 0.25em 0.5em;
}

/*评分栏变色*/

.page-rate-widget-box {
    border-radius: 10px;
    display: inline-block;
    margin-bottom: 10px;
}
.page-rate-widget-box .rate-points {
    background-color: #272A31 !important;
    border: solid 1px #272A31;
    border-right: 0;
    border-radius: 12px 0 0 0px;
}
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: #272A31;
    border-top: solid 1px #272A31;
    border-bottom: solid 1px #272A31;
    font-weight: bold;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: #272A31;
    color: #FFFFFF;
    padding: 0 4px;
    margin: 0 1px;
    transition: 0.125s;
}
.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: #FFFFFF;
    color: #272A31;
    text-decoration: none;
}
.page-rate-widget-box .cancel {
    background-color: #272A31;
    border: solid 1px #272A31;
    border-left: 0;
    border-radius: 0 5px 5px 0;
}
.page-rate-widget-box .cancel a {
    background: transparent;
    text-transform: uppercase;
    color: #d0d0d0;
    transition: 0.125s;
}
.page-rate-widget-box .cancel a:hover {
    border-radius: 0 3px 3px 0;
    color: #ffffff;
    text-decoration: none;
}

.yui-navset .yui-content {
    border-bottom: 4px solid rgb(66, 66, 72);
    border-top-color: transparent;
    padding: 0.25em 0.5em;
    border-left: transparent;
}

#page-content .yui-content::after {
    content: "附录结束";
    font-weight: bold;
    font-size: 95%;
    color: white;
    background: rgb(66, 66, 72);
    width: 150px;
    border-radius: 3px;
    display: block;
    text-align: center;
    margin: auto;
    margin-bottom: -5px;
}

