content:
[[module css]]

div#container-wrap {
    background: linear-gradient(#13ff00b0 39px ,#000 40px ,#ffffff 41px ,#ffffff 180px,#459DF5 180px,#459DF5 222px,#ffffff 222px,#ffffff,#ffffff);
    background-size: 1px;
}

div#header h1 a:before {
    content: "B - E - T - A";
    color: rgb(53, 53, 53);
    font-size: 50px;
    font-family: Noto Sans SC;
}

#search-top-box-form input[type=submit]:hover {
    border: solid 1px #000;
    color: #fff;
    text-shadow: none;
    background: #317EF3;
    background-color: #3388FF;
    box-shadow: 0 1px 3px rgba(0,0,0,.8);
}

#search-top-box-input:hover {
    border: solid 1px #fff;
    color: #fff;
    background-color: #fff;
    box-shadow: inset 1px 1px 3px rgba(0,0,0,.8);
}

div#header h2 ::before {
    content: "生灵 - 自然 - 万物";
    color: rgb(0, 255, 57);
    font-size: 20px;
}

#header {
    height: 200px;
    position: relative;
    z-index: 10;
    padding-bottom: 10px;
    background: url(https://scpsandboxcn.wikidot.com/local--files/chris-evertworth/betafinalblack.svg) 15vw 35px no-repeat;
    background-size: 150px;
}

#login-status {
    color: #000;
    font-size: 90%;
    z-index: 30;
}

#login-status a {
    background: transparent;
    color: #000;
}

#header h1 a {
    display: block;
    margin: 0;
    padding: 76px 0 25px;
    line-height: 0px;
    max-height: 0;
    color: #eee;
    background: transparent;
    font-family: BauhausLTDemi,Simhei,Arial,sans-serif;
    font-size: 0%;
    text-decoration: none;
    text-shadow: 3px 3px 5px #fff;
    letter-spacing: 0.9px;
}

#header h1, #header h2 {
    margin-left: 0;
    float: none;
    text-align: center;
}

#header h2 span {
    font-size: 0%;
}

#search-top-box-form input[type=submit] {
    border-radius: 0px;
}

input.text {
    border: 1px solid #777;
    margin: 0 0px;
    padding: 5px 11px;
    background-color: #FFF;
    color: #000;
}

#search-top-box-form input[type=submit] {
    padding: 4.8px 15px;
    color: #fff;
    background-color: #3388FF;
    background: linear-gradient(to bottom,#3388FF,#3388FF,#3388FF);
    box-shadow: 0 1px 3px rgba(255, 255, 255, 0);
}

#search-top-box-input {
    background-color: #fff;
    box-shadow: none;
}

input.button {
    margin: 0 0px;
    padding: 0px 0.5em;
    text-decoration: none;
    background-color: #F4F4F4;
    border: 1px solid #AAA;
    color: #000;
}

#top-bar {
    position: absolute;
    top: 190px;
    right: auto;
    width: 100%;
    height: 21px;
    line-height: 18px;
    padding: 0;
    margin: 0 auto;
    z-index: 20;
    font-size: 90%;
    justify-content: left;
    display: flex;
}

#search-top-box {
    position: absolute;
    top: 140px;
    right: 29vw;
    width: 250px;
    text-align: right;
}

#search-top-box-input {
    border-radius: 0px;
}

[[/module]]
[[include :scp-wiki:component:toggle-sidebar]]

