[[module CSS]]
@import url(https://scp-jp.github.io/files/util/common/component/imageInjunction/divider.css);

:root {
    --header-title: "SITE-81ET";
    --header-subtitle: "人を呪わば穴ふたつ";
    --bg: 180, 180, 180;
    --body-bg: 250, 249, 252;
    --main-accent: 186, 167, 204;
}

div#extra-div-1 {
    background-image: url(http://scp-jp-storage.wdfiles.com/local--files/file%3A3438721-302-js60/Site-81ET.svg);
    filter: opacity(20%);
}

span:is(.ut, .st) { font-weight: bold; color: rgb(var(--main-accent)); }
span:is(.st) { text-decoration: underline 2px; text-underline-offset: 3px; }

/* Rateモジュールを中心に移動 */

.creditRate {
float: unset !important;
margin-bottom: unset !important;
}

.box1{ center no-repeat ; float: center; border: solid 2px #000000; padding: 1px 15px; margin: 10px auto; }
.jotting.tableb table td { background-color: rgb(var(--body-bg)); }
@media (max-width:700px){ div:is(.jotting, .transcript) { padding-inline: 15px;}}

.box3 { padding: 15px; background: rgba(var(--body-bg)); position: relative; padding-top: 2rem; }
.box3:before { content: '';position: absolute;width: 100%;height: 20px;top: 0;left: 0;border: solid 2px #5a5a5a;border-bottom: none !important;box-sizing: border-box; }
.box3:after { content: '';position: absolute;width: 100%;height: 20px;bottom: 0;left: 0;border: solid 2px #5a5a5a;border-top: none !important;box-sizing: border-box; }


.box4 {
    position: relative;
    max-width: 300px !important;
    width: 100%;
    font-size: 90%;
    padding: 10px;
    margin: 1vh auto;
    box-sizing: border-box;
    border-top: solid 4px rgb(var(--main-accent));
    background: rgb(255, 250, 240);
}

.container {
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
}

.imagediv {
    float: left;
    margin-left: 10px;
    margin-top: 25px;
    position: absolute;
    width: 130px;
}

@media (max-width: 780px) {
  .imagediv {
    float: none;
    text-align:center;
    margin: auto;
    margin-top: 30px;
    position: static;
    width:160px;
  }
}
[[/module]]
