[[module css]]
body{
    --barColour: #EB6123;
}

:root{
  --header-title: "SCP FOUNDATION";
  --header-subtitle: "Spook, Creep, Pester";
  --lgurl: url("http://scp-wiki.wdfiles.com/local--files/black-autumn-hub/scp-halloween-alone.png");
  --accentColor: #EB6123;

}

div#extra-div-1 {
    filter: opacity(40%);
}

.quote-text {
    display: inline-block;
    border:dashed 1px #999999;
    background:#5c5c5c;
    float:center;
    width:84%;
    padding:1px 15px;
    margin: 10px 10px 10px 30px
}

div#extra-div-1 {
    background-image: url(https://scp-wiki.wdfiles.com/local--files/component:pride-highlighter/nb_logo_blk.svg);
    filter: opacity(25%);
}

#page-content .info-container {
    --link-color: rgb(var(--swatch-text-general));
}

#page-content .info-container .translation_block {
    display: none;
}

#page-content .info-container .collapsible-block-link,
#page-content .info-container:hover .collapsible-block-link {
    line-height: 1.6em;
}

#page-content .info-container .collapsible-block-folded,
#page-content .info-container .collapsible-block-unfolded-link {
    display: grid;
}

#page-content .info-container .collapsible-block-folded .collapsible-block-link,
#page-content .info-container .collapsible-block-link {
    -webkit-mask: unset;
            mask: unset;
    background: transparent;
    grid-column: 1;
    grid-row: 1;
    opacity: 0;
}

#page-content .info-container .collapsible-block-folded::before,
#page-content .info-container .collapsible-block-unfolded-link::before {
    --wght: 800;
    content: "Elenee FishTruck";
    color: #FFFFFF;
    font-family: Inter, Arimo, Verdana, Geneva, "Helvetica Neue", "Helvetica", Arial, sans-serif;;
    display: block;
    pointer-events: none;
    grid-column: 1;
    grid-row: 1;
    line-height: 2.75ex;
}
