[[module CSS]]
.thread {
    background:#f2f3f7;
    padding:10px;
    border:1px solid #b5b5b5;
}

.thread .th_title {
    color:#485269;
    font-size:18px;
    font-weight:700;
    padding-bottom: 5px;
}

.thread .th_top {
    border:1px solid #cecece;
    background:#fff;
    width: 31.5%;
    text-align:center;
    display: inline-block;
    padding: 10px;
}
.thread .th_top:not(first-of-type) {
    margin-top: -1px;
}
.th_top_wrap {
    display: flex;
    flex-wrap: no-wrap;
    justify-content: center;
}

.thread .th_top:hover {
    border:1px solid #fff;
}
@media (max-width: 979px) {
.thread .th_top {
    width: 92%;
    display:block;
}
.th_top_wrap {
    flex-wrap: wrap;
}
}

.talk {
    border-width:1px;
    display:inline-block;
    border-style:none solid solid none;
    border-color:#ddd;
    background-color:#efefef;
    margin-bottom:8px;
    padding:8px !important ;
    font-size:120% !important ;
}
.talk-top {
    margin-bottom: 15px !important ;
}
.talk-post {
    margin-top: 5px;
    margin-bottom: 5px;
}
.post a{
    color:blue;
    text-decoration:underline!important;
}
.thread a{
    color:blue;
}
.thread a:hover {
    color:#5e62ff;
}

.balloonoya:hover .balloon {
  display: inline-block;
}
.balloon {
    position:absolute;
    border:1px solid #000;
    display:none;
    background-color:#efefef;
    padding:8px !important;
}
.balloon:after{
  content: "";
  position: absolute;
}
.post {
    font-size: .9em;
}
[[/module]]
