<script type="text/javascript">
async function getxjoch(username, userId) {
    await $.ajax({
      url: "https://zh.xjo.ch/wd/userinfo",
      crossDomain: true,
      dataType: 'json',
      timeout: 60000,
      data: {username: username, userId: userId},
      success: function( res ) {
        if (res.data!==null) {
          $('#content').html(`<p>总编辑数： ${res.data.revisions}</p>
<p>总发帖数： ${res.data.posts}</p>`);

        } else {
          $('#content').html(`错误: ${res.message}`);
        }
      },
      error: function(a,b,c) {
        $("#content").html(`错误:：${JSON.stringify(a)}
请检查开发人员工具、控制台日志以获取更多信息。 `);
      }
    })
  }

function getinfo() {
  var username = null, userId = null;
  if (!!UID.value.trim()) { userId = UID.value.trim() }
  if (!!UName.value.trim()) { username = UName.value.trim() }
  if (!username&&!userId) {
    return $('#content').text(`Please specify user.`);
  }
  $('#content').text(`正在获取信息，若请求的用户有很多修订或帖子，请耐心等待... `);
  getxjoch(username, userId);
}

$(function(){

   $('#refresh').html(`
<p>把你的用户名输入于此: <input type="text" id="UName" value="" style="display: inline-block; width: 30%;" /><br/>
或者把你的Wikidot ID输入于此: <input type="text" id="UID" value="" style="width: 30%;" /></p>
<p><input type="button" value="获取信息" onclick="getinfo()" /></p>`);

var UName = document.getElementById("UName");
var UID = document.getElementById("UID");

});
</script>

</head>
<body>
