/* Japanese Localizing */

@import url('https://fonts.googleapis.com/css2?family=Noto+Sans+JP:wght@900&amp;display=swap');
@import url('https://fonts.googleapis.com/css2?family=IBM+Plex+Sans+JP:wght@300;700&amp;display=swap');
@import url('https://fonts.googleapis.com/css2?family=BIZ+UDPGothic:wght@700&display=swap');

:root {

--header-title: "訓戒" !important;

}

.admohub-eptitle {
font-family: 'Noto Sans JP', var(--header-font);
font-weight: 900;
}

.admohub-epdirector::before {
font-family: 'IBM Plex Sans JP';
font-weight: 300;
}

.admohub-epno {
font-family: 'Roboto Condensed', 'IBM Plex Sans JP', var(--UI-font);
}

a.admohub-eplink, a.admohub-accesslink {
font-family: 'IBM Plex Sans JP', 'Roboto Condensed',var(--UI-font);
}

.admohub-quote {
font-family: 'BIZ UDPGothic', 'Sofia Sans', var(--UI-font);
}

.creditRate{
display: inline-flex !important;
}

.rate-box-with-credit-button .creditButton {
background-color: transparent !important;
border: 0px !important;
}

.creditButton a {
color: #fff6f0 !important;
}

.modalcontainer {
position: absolute !important;
top: 0 !important;
bottom: 0 !important;
left: 0 !important;
right: 0 !important;
width: 400px !important;
height: 200px !important;
margin: auto !important;
}

.modalbox{
height: auto !important;
}

.modalbox:after {
height: 60px !important;
}

.credit table td {
border: none;
padding: 0 .25rem !important;
}

#top-bar .open-menu a {
background: transparent !important;
}

