[[module CSS]]

/* Header */
div#container-wrap {
    background: url('http://scpsandboxcn.wikidot.com/local--files/meowait/synthwave1') center top repeat-x;
    background-size: 1000px;
}

#header {
    height: 290px;
    background-image: url('http://scpsandboxcn.wikidot.com/local--files/meowait/scarletkinglogo1');
    background-position: 50% 45%;
    background-repeat: no-repeat;
    background-size: 340px auto;
}

#header h1,
#header h2 {
    margin-left: 145px;
}

#header h1 a span,
#header h2 span {
    display: none;
}

#header h1 {
    max-height: 125px;
}

#header h1 a {
    padding-top: 105px;
    color: #2C77C1;
    font-family: 'Vollkorn SC', sans-serif;
    font-size: 70px;
    font-weight: normal;
    letter-spacing: -5px;
    text-shadow: -2px 0 #ffffff, 0 2px #ffffff, 2px 0 #ffffff, 0 -2px #ffffff;
}

#header h1 a::before {
    content: none;
}

#header h2 {
    font-size: 0px;
}

/* Login & Search & Top Bar */
#login-status {
    position: absolute;
    top: 3px;
    color: #ffffff;
    z-index: 2;
}

#login-status a {
    color: #ffffff;
    font-weight: bolder;
}

#account-topbutton {
    border-color: #ffffff;
}

#login-status ul a {
    color: #000000 !important;
}

#search-top-box {
    position: absolute;
    top: 30px;
    right: 0;
    width: 250px;
    text-align: right;
    z-index: 1;
}

#search-top-box-form {
    display: inline-flex;
}

#search-top-box-form > input {
    -webkit-appearance: none;
    -webkit-border-radius: 0;
    border-radius: 5px;
}

#search-top-box-input {
    background-color: 950d9a;
    border: solid 1px 950d9a;
    color: #ff52ed;
}

#search-top-box-input:hover,
#search-top-box-input:focus {
    box-shadow: inset 1px 1px 5px rgb(0 0 0 / 50%);
    color: #000000 !important;
}

#search-top-box-form input[type=submit] {
    padding: 2px 5px;
    background: #61097a;
    box-shadow: 0 1px 3px rgba(0, 0, 0, .5);
    border: solid 1px #ffffff;
    color: #ffffff;
    font-size: 90%;
}

#search-top-box-form input[type=submit]:hover,
#search-top-box-form input[type=submit]:focus {
    background: #DBBFA6;
}

#top-bar {
    display: flex;
    justify-content: center;
    top: 277px;
    width: 100%;
    z-index: 20;
}

#top-bar a {
    color: #ffffff;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: #9947a5;
    border-color: transparent;
    color: #ffffff;
}

#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: #844682;
}

#top-bar ul li ul,
#top-bar ul li ul li ul {
    border-color: rgba(0, 0, 0, .4);
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-color: rgba(0, 0, 0, .1);
}

#top-bar ul li.sfhover ul li ul a,
#top-bar ul li:hover ul li ul a {
    border-top: none;
}

.top-bar {
    position: relative;
    margin: 0 auto;
}
.c_topbar_arrow {
    color: #92ffff;
}

/* Page Elements */
#page-title,
.meta-title {
    color: #7a0976;
    font-size: 2.1em;
    font-weight: bold;
}

#content-wrap {
    margin: 30px auto 0;
}

h1, h2, h3, h4, h5, h6 {
    color: #61097a;
    font-weight: bold;
}

/* ---- SCROLLBAR ---- */
::-webkit-scrollbar {
    width: unset;
    height: unset;
}

::-webkit-scrollbar-track {
    background: #f5e9f3;
}

::-webkit-scrollbar-thumb {
    background: #7a0969;
    border: none;
}

::-webkit-scrollbar-thumb:hover {
    background: #a00c94;
}

textarea::-webkit-scrollbar-track {
    background: #f5e9f1;
}

/* Side Bar */
#side-bar {
    background: none;
}

#side-bar .side-block,
#side-bar .side-block.media,
#side-bar .side-block.resources {
    background-color: #f5e9f4;
    border: 2px solid #73097a;
    border-radius: 10px 0;
}

#side-bar .heading,
#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #000000;
    color: #000000;
}

#side-bar .collapsible-block-folded-link .collapsible-block-link:hover,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #967b99;
}

#side-bar .collapsible-block-folded-link .collapsible-block-link,
#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    color: #000000;
}

#side-bar .menu-item .image,
#side-bar .collapsible-block-folded,
#side-bar .collapsible-block-unfolded-link .collapsible-block-content .image {
    filter: hue-rotate(200deg);
}

#side-bar .collapsible-block-folded a:link {
    filter: hue-rotate(-200deg);
}

#side-bar a:link {
    color: #8a2399;
}

#side-bar a:visited {
    color: #725a6e;
}

#side-bar a:link:hover,
#side-bar a:visited:hover {
    color: #725a6e;
}

.scpnet-interwiki-frame {
    filter: invert(1) hue-rotate(245deg) saturate(.8);
}

#top-bar .open-menu a {
    border-color: #000000;
    color: #000000;
}

/* Tab */
div.yui-navset ul.yui-nav a,
div.yui-navset ul.yui-nav a:hover,
div.yui-navset ul.yui-nav .selected a,
div.yui-navset ul.yui-nav .selected a:hover,
div.yui-navset ul.yui-nav .selected a:focus {
    background: none;
    border: none;
}

.yui-navset {
    clear: both;
    margin-bottom: 1em;
    box-shadow: 0 1px 2px rgba(0, 0, 0, .5);
}

.yui-navset .yui-nav {
    border: 1px solid gainsboro;
    border-bottom:#000000;
}

.yui-navset .yui-nav li {
    margin-right: 10px !important;
}

.yui-navset .yui-nav li:first-child {
    margin-left: 1.5em;
}

.yui-navset .yui-nav li a em {
    padding: 10px .75em .25em;
    margin-bottom: -1px;
    background: none;
    border: none;
    border-bottom: 4px solid transparent;
    color: gray;
    transition: .2s;
}

.yui-navset .yui-nav li a em:hover {
    background:#000000;
}

.yui-navset .yui-nav .selected a em,
.yui-navset .yui-nav .selected a em:hover {
    padding: 10px .75em .25em;
    background: none;
    border: none;
    border-bottom: 4px solid #df1bfd;
    color: #d72aca;
    font-weight: bold;
}

div.yui-navset div.yui-content {
    background: #385481;
    border: 1px solid gainsboro;
}

/* Rate Module */
.page-rate-widget-box {
    margin-right: 0;
    border: 2px solid #a745ad;
    border-radius: 5px;
    box-shadow: none;
}

.page-rate-widget-box > span {
    border: none !important;
}

.page-rate-widget-box .rate-points {
    background: #e5b7e2 !important;
    color: #000000 !important;
}

.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background: #e5b7e2;
}

.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background-color: #a045ad;
    color: #e5b7e0;
}

.page-rate-widget-box .cancel {
    background: #e5b7e0;
}

.page-rate-widget-box .cancel a {
    color: #000000;
    font-weight: bold;
}

.page-rate-widget-box .cancel a:hover {
    background: #AD8C45;
    border-radius: 0;
    color: #e4b7e5;
}

.creditRate {
    margin-right: 0 !important;
}

.rate-box-with-credit-button {
    background: #e5b7de !important;
    border: 2px solid #a945ad !important;
    box-shadow: none !important;
}

.rate-box-with-credit-button .page-rate-widget-box {
    border: none;
}

.creditButton p a {
    border-left: none !important;
    color: #000000 !important;
}

#page-content .rate-box-with-credit-button .fa-info {
    padding: 3px 0 1px;
}

#page-content .rate-box-with-credit-button .fa-info:hover {
    background: #ad45a7;
    border-radius: 0 3px 3px 0;
    color: #e5b7e4 !important;
}

/* ---- EDITING WINDOWS ---- */
.owindow {
    background-color: #ffffff;
    border-color: #ad45a7;
}

.owindow .modal-header {
    background-color: #ffffff;
}

.owindow .modal-body img {
    background-color: #ffffff !important;
    color: #000000;
}

.owindow .title {
    background-color: #ffffff;
}

.owindow a,
.owindow a:visited {
    color: #ce8cc5;
}

.owindow .active {
    color: #000000;
}

.owindow .content.modal-body img {
    background-color: #725a6e !important;
}

.owindow .button-bar a {
    background-color: #ffffff;
    color: #000000;
}

.owindow .button-bar a:hover,
.owindow .button-bar a:focus {
    background-color: #ffffff !important;
    border-color: #997b98;
    color: #000000;
}

table.page-files .highlight {
    background-color: #591459;
}

.wiki-content-table tr th {
     background: #591459;
     color: #ffffff;
     border-color: #000000;

input.text,
input.text:focus,
input.text:hover {
    background-color: #ffffff !important;
    border-color: #997b99;
    color: #000000;
}

textarea {
    background-color: #FEFFFF;
    color: #000000;
}

.change-textarea-size a {
    background-color: #ffffff;
    color: #000000;
}

#lock-info {
    background-color: #ffffff;}

/* Blockquote */
blockquote,
div.blockquote {
    background-color: #faeeff;
    border-style: dotted;
}

/* etc. */
@media (min-width: 581px) and (max-width: 767px) {
    #header {
        background-size: 300px auto;
    }
}

@media (min-width: 480px) and (max-width: 580px) {
    #header {
        background-size: 260px auto;
    }
}

@media (min-width: 321px) and (max-width: 479px) {
    #header {
        background-size: 240px auto;
    }
}

@media (max-width: 320px) {
    #header {
        background-size: 210px auto;
    }
}

