content:
[[module css]]
#header {
    height: 140px;
    position: relative;
    z-index: 10;
    padding-bottom: 22px; /* FOR MENU */
    background: url(http://scpsandboxcn.wikidot.com/local--files/mscarlet-work-3/Touhei2.png) 10px 40px no-repeat;
}

#header h1 a {
    display: block;
    margin: 0;
    padding: 80px 0 25px;
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    background: transparent;
    font-size: 120%;
    text-shadow: none;
    text-decoration: none;
}

#header h1 a::before {
    content: "東弊重工";
    color: #FFFFFF;
    background: transparent;
    font-family: BauhausLTDemi, Arial, sans-serif;
    font-size: 150%;
    text-decoration: none;
    text-shadow: 3px 3px 5px #000000;
}

#header h2 span {
    display: block;
    margin: 0;
    padding: 19px 0;
    line-height: 0px;
    max-height: 0px;
    color: transparent;
    text-shadow: none;
}

#header h2 span::before {
    content: '映畫部門';
    color: #f0f0c0;
    text-shadow: 1px 1px 1px #000;
    text-shadow: 1px 1px 1px rgba(0,0,0,.8);
}
[[/module]]

[[>]]
[[module Rate]]
[[/>]]

