[[module CSS]]

/* Layout */

/* HEADER */
#header h1 a {
    font-family: "BauhausLTDemi", "ヒラギノ角ゴ Pro W6", HiraKakuPro-W6, sans-serif;
}

#header h2 span {
    font-family: "ヒラギノ角ゴ Pro W6", HiraKakuPro-W6, sans-serif;
}


#page-content .grid {
    display: flex;
    overflow: auto;
}

#page-content .grid-left {
    float: left;
    width: 50%;
    text-align: left;
    overflow: auto;
}

#page-content .grid-right {
    float: right;
    width: 50%;
    text-align: left;
    overflow: auto;
}

#page-content .card {
    background-color: #fff;
    padding: 0.2em 0;
    text-align: center;
    font-size: 1.2em;
}

#page-content .card-1 {
    float: center;
    border-top: solid #B7B7B7 1px;
    padding: 1em;
    overflow: auto;
}

#page-content .image-card{
    position: relative;
    margin: 1px 0;
}

#page-content .image-card-text{
    position: absolute;
    bottom: 0;
    left: 0;
    padding: 0 1em;
    width: 100%;
    background: rgba(0, 0, 0, 0.5);
    color: #fff;
    font-size: 80%;
    text-align: center;
    box-sizing: border-box;
}

#page-title{
    display:none;
}

.fake_title {
  font-size: 200%;
  color: #990011;
  padding: 0 0 0.25em;
  margin: 0 0 0.6em;
  border-bottom: 1px solid #bbb;
  font-weight: normal;
}

.fake_title p {
  padding: 0 0 0 0;
  margin: 0 0 0 0;
}


/* Mobile Media Query */
@media (max-width: 479px) {

#page-content .grid {
    display: block;
}

#page-content .grid-left,
#page-content .grid-right {
    width: 100%;
    float: left;
    text-align: left;
}
}

/* Note Media Query */
@media (min-width: 480px) and (max-width: 580px) {

#page-content .grid {
    display: block;
}

#page-content .grid-left,
#page-content .grid-right {
    width: 100%;
    float: left;
    text-align: left;
}
}

/*CustomCollapsible layout */

.CustomCollapsible{
    padding: 0;
    margin: 0;
    text-align: left;
    background-color: #fff;
}

.CustomCollapsible .collapsible-block-link {
    display: flex;
    padding-left: 1em;
    height: 3.5em;
    text-decoration: none;
    font-size: 105%;
    color: #CDCDCD;
    align-items: center;
    justify-content: left;
    background-color: #444;
    border-top: solid #333 1px;
}

.CustomCollapsible .collapsible-block-link:hover {
    background-color: #555;
    color: #fff;
}

.CustomCollapsible .collapsible-block-link:active {
    background-color: #555;
    color: #fff;
}




/* CustomTab */
.CustomTab {
    border: solid #333 1px;
}

.CustomTab .yui-navset .yui-content{
    background: none;
    border-top: solid #333 1px;
    border-right: none;
    border-bottom: none;
    border-left: none;
    padding:0;
    margin:0;
    overflow: auto;
}

.CustomTab .yui-navset ul {
    display: flex;
    border: solid #333 1px;
    border-bottom: none;
    padding:0.3em 1em;
    margin:0;
    background: #333;
    height: 3em;
    align-items: center;
}

.CustomTab .yui-navset .yui-nav a,
.CustomTab .yui-navset .yui-navset-top .yui-nav a {
    background: none;
    color: #B7B7B7;
    border: none;
    overflow-y:hidden;
}

.CustomTab .yui-navset .yui-nav .selected a,
.CustomTab .yui-navset .yui-nav .selected a:focus, /* no focus effect for selected */
.CustomTab .yui-navset .yui-nav .selected a:hover { /* no hover effect for selected */
    background: none;
    color: #fff;
    border: none;
    overflow-y:hidden;
}

.CustomTab .yui-navset .yui-nav a:hover,
.CustomTab .yui-navset .yui-nav a:focus {
    background: none;
    color: #B7B7B7;
    border: none;
    overflow-y:hidden;
}

.CustomTab .yui-navset .yui-nav,
.CustomTab .yui-navset .yui-navset-top .yui-nav {
    border: none;
}

.CustomTab .yui-navset .yui-nav,
.CustomTab .yui-navset .yui-navset-top .yui-nav {
    border: none;
}

[[/module]]
