[[module CSS]]
/* HELP! AIKO IS HOLDING ME CAPTIVE AND IS FORCING ME TO WRITE CSS!!! SAVE ME!!!
~ Hexick
*/
:root {
    --lgurl: url("http://scp-wiki.wikidot.com/local--files/aiko-artpage/logo");
}
a:link {
    color: #7662d1;
}
a:visited {
    color: #2a1f59;
}
a:hover {
    background: #ffdd6e !important;
}
#main-content {
    border-color: #7662d1;
}
#header h1 a:hover {
    background: transparent !important;
}
#top-bar ul li a {
    color: #7662d1;
    border-left: solid 1px #7662d1;
    border-right: solid 1px #7662d1;
}
#top-bar ul li a {
    background-image: linear-gradient(to bottom, rgba(167,215,249,0) 0, rgba(255, 238, 184, 1) 100%);
}
div#extra-div-2::before {
    content: var(--header-title, "Garden of Dreams");
    color: #2a1f59;
    top: 0.9rem;
    left: 18rem;
}
div#extra-div-2::after {
    content: var(--header-subtitle, "You are forever loved");
    color: #7662d1;
    top: 6rem;
    left: 24.5rem;
}
   #header h1 a::before {
        content: var(--header-title, "Garden of Dreams");
        color: #2a1f59;
        top: 0.9rem;
        left: 18rem;
    }
    #header h2::before {
       content: var(--header-subtitle, "You are forever loved");
       color: #7662d1;
       top: 6rem;
       left: 24.5rem;
    }
    div#extra-div-2::before, div#extra-div-2::after { display: none; }
}
.yui-navset .yui-nav .selected a{
    background: #ffdde3 !important;
    color: #2a1f59 !important;
}
.yui-navset .yui-nav, .yui-navset .yui-navset-top .yui-nav {
    border: solid #ffdde3;
    border-width: 0 0 5px;
    Xposition: relative;
    zoom: 1;
    color: #ffdde3;
}
