<style type="text/css">
@import url(http://scp-jp.wdfiles.com/local--files/japanese-syntax/scp-WikidotCss.css);

#lastans{
margin: 10px;
}

#crossword table,
#lastans table{
border-collapse:  collapse;
border-spacing: 0;
}

#crossword td,
#lastans td{
width: 2rem;
height: 2rem;
border: solid #333 2px;
position: relative;
background: #FFF;
}

#crossword td.block{
background: #333;
}

#crossword td.block .str{
display: none;
}

#crossword td.key::before,
#lastans td.key::before{
content: "";
width: 90%;
height: 90%;
border: solid #333 2px;
box-sizing: border-box;
position: absolute;
top: 5%;
left: 5%;
}

#crossword td .number{
font-size: 0.4rem;
width: 0.8rem;
height: 0.8rem;
text-align: center;
position: absolute;
top: 0;
left: 0;
background: #FFF;
}

#crossword td .key,
#lastans td .key{
font-size: 0.4rem;
width: 0.8rem;
height: 0.8rem;
text-align: center;
position: absolute;
bottom: 0;
right: 0;
