  [1, 2, 0, 0, 3, 4, 0, 5, 0, 6],
  [0, 0, 0, 7, 0, 0, 0, 0, 0, 0],
  [8, 0, 0, 0, 0, 0, 9, 0, 10, 0],
  [0, 0, 0, 0, 0, 11, 0, 12, 0, 0],
  [13, 0, 14, 0, 15, 0, 0, 0, 0, 16],
  [0, 0, 0, 0, 0, 0, 17, 0, 18, 0],
  [0, 19, 0, 20, 0, 21, 0, 0, 0, 0],
  [22, 0, 0, 0, 0, 0, 0, 23, 0, 0]
];
