[[module CSS]]

:root{
  --header-title: "SCP基金会";
  --head-font: var(--ui-font);
}

.addendum {
    border:solid 2px #999999;
    background:#F5F5F5;
    width:84%;
    padding:1px 15px;
    margin: 10px auto;
}

.footnotes-footer {
display:none;
}

.buttonthing {
  display: flex;
  justify-content: center;
}

.blink {
  animation: blink 1.8s infinite;
}

@keyframes blink {
  0% {
    opacity: 1;
  }
  50% {
    opacity: 0;
  }
  100% {
    opacity: 1;
  }
}

@media only screen and (max-width: 600px) {

.buttonthing {
  -ms-transform: translateX(15%);
  transform: translateX(15%);
}

}

[[/module]]
