     "オズワルド＝ダスト",
     "カール・デイリー",
     "キャロライン・アミナ・ロスタム",
     "ゲイル・マクラーレン",
     "ジョアンナ＝フェッロ",
     "トーマス・ベイリー",
     "フォンセカ・マリア",
     "ブレア・アーバナ",
     "ラック・オブ・ライム",
     "リリー＝サイアンス",
     "レベッカ＝サンダース",
     "ローランド＝コービン",
     "ヨセフ・アッバシ"
     ];

     // Util: Random Number Func
     function Random(n) {
     return Math.floor(Math.random() * n);
     }

     // Get Random Date Func
     // 参考: https://www.codegrepper.com/code-examples/clojure/get+random+date+from+past+year+javascript
     function GetRandTime() {
     let dob;

     //set a range of years
     let min = 1940;
     let max = 2020;

     // Math.ceil prevents the value from being 0;
     let year = Math.floor(Math.random() * (max - min) + min);
     let month = Math.ceil(Math.random() * 12);
     let day = Math.ceil(Math.random() * 28);

     //this ensures that the format will stay yyyy/MM/dd;
     if (month < 10) {
     month = "0" + month;
     }
     if (day < 10) {
     day = "0" + day;
     }
     //concatenates random dob in yyyy/MM/dd format;
     dob = year + "/" + month + "/" + day;

     return dob;
     }

     // Add Month Func
     function AddMonth(DateStr, AddNum) {
     const DateStrAry = DateStr.split("/", 3);
     let YearStr = DateStrAry[0];
     let MonthStr = DateStrAry[1];
     let DayStr = DateStrAry[2];
     let Year = Number(YearStr);
     let Month = Number(MonthStr);
     let Day = Number(DayStr);

     // Add Day
     Day = Day + 1;

     // Add Month
     Month = Month + AddNum;
     if (Month > 12) {
     Year = Year + 1;
     Month = Month - 12;
     }

     // Convert To String
     let RetYearStr = String(Year);
     let RetMonthStr = String(Month);
     if (Month < 10) {
     RetMonthStr = "0" + RetMonthStr;
     }
     let RetDayStr = String(Day);
     if (Day < 10) {
     RetDayStr = "0" + RetDayStr;
     }

     DateStrAry[0] = RetYearStr;
     DateStrAry[1] = RetMonthStr;
     DateStrAry[2] = RetDayStr;
     return DateStrAry.join("/");
     }

     // Get Random Person Name
     function GetRandPersonName() {
     let RandIndex = Random(NamesArray.length);
     let NameStr = NamesArray[RandIndex];
     return NameStr;
     }

     // Get Random Sys Name
     function GetRandSysName() {
     let RandIndex = Random(SysNamesArray.length);
     let SysNameStr = SysNamesArray[RandIndex];
     return SysNameStr;
     }

     //Change Sentence Func
     function Change() {
     // Get Random Content
     let RandomDate1 = GetRandTime();
     let RandomDate2 = AddMonth(RandomDate1, 3);
     let RandomDate3 = AddMonth(RandomDate2, 1);
     let RandomName1 = GetRandPersonName();
     let RandomName2 = GetRandPersonName();
     let RandomSysName = GetRandSysName();

     // Set Content
     document.getElementById("RandTime1").innerHTML = RandomDate1;
     document.getElementById("RandTime2").innerHTML = RandomDate2;
     document.getElementById("RandTime3").innerHTML = RandomDate3;
     document.getElementById("RandPerson1").innerHTML = RandomName1;
     document.getElementById("RandPerson2").innerHTML = RandomName2;
     document.getElementById("RandSystem").innerHTML = RandomSysName;

     // Set Random Interval
     clearInterval(iid);
     const max = 30;
     const min = 1;
     let randratio = Math.floor(Math.random() * (max - min + 1) + min);
     iid = setInterval("Change()", BaseInterval / 2 + 1000 * randratio);
     }

     var iid = setInterval("Change()", BaseInterval);
     </script>
