/* Import Google Font */
@import url("https://fonts.googleapis.com/css2?family=Noto+Serif+JP&display=swap");

/* Erase Background-Components */
#page-title,
#header,
#side-bar {
    display: none;
}
#main-content {
    margin: 0 4em 0 12em;
}
div#container-wrap {
    background: none;
}
@media (max-width: 979px) {
    #page-content div,
    #page-content div table {
     float: none;
    }
}
@media (max-width: 767px) {
    #main-content {
     margin: 0 5% !important;
    }
}

/* background gradation */
body {
    background: linear-gradient(to bottom, white 15%, black 75%);
}

/* Chars on Image */
.hyou {
    border: none;
    width: calc(100% + 2px);
    max-width: none !important;
    position: absolute;
    left: -1px;
    margin: 0 auto;
    bottom: 0;
    color: white;
    background: rgba(0, 0, 0, 0.5);
}

.cell {
    text-align: center;
    transform: scale(0.8, 1.1);
    transform-origin: center;
    font-family: "Noto Serif JP", MS Mincho, ヒラギノ明朝 ProN W3, sans-serif;
}
@media (max-width: 479px) {
    .cell {
     font-size: 100%;
    }
}
@media (min-width: 480px) and (max-width: 580px) {
    .cell {
     font-size: 125%;
    }
}
@media (min-width: 581px) {
    .cell {
     font-size: 140%;
    }
}

/* 独白吹き出し */
/* 参考: http://ja.scp-wiki.net/scp-1669-jp */
.outside {
    margin: 2em -2em;
    padding: 1em 4em;
    background: radial-gradient(ellipse closest-side, black 0%, transparent 100%);
    z-index: -2;
}

.outside.inline {
    margin: 1em -2em;
    padding: 1px 4em;
}
[[/module]]

