[[module CSS]]
/* 黒板 */
.blackboard {
 position: relative;
 min-height: 120px;
 margin: 0 -5px 2px 10px;
 font-size: 0.9rem;
 color: #fff;
 border: 2px solid #000;
 border-radius: 5px;
 box-shadow: 0 0 0 4px #fff inset;
 background-color: rgba(0,0,0,0.9);
 padding: 0rem 0.7rem;
 font-family: 'Andale Mono', 'Courier New', Courier, monospace !important;
}
.blackboard.answer p {
 margin-left: 1.7rem;
}
.blackboard .blink-before {
 font-size: 1.2rem;
}
.blackboard a {
 text-decoration: none;
 color: #fff;
 font-family: 'MS UI Gothic';
}
.blackboard .blink-before::before {
 content: '▶';
 position: absolute;
 left: 1rem;
 animation: blink 1.4s infinite;
}
.blackboard p:last-of-type {
 margin-bottom: 0;
}
/* 回り込み */
.float-r {
 float: left;
}
/* 回り込み解除 */
.clearfix {
 zoom: 1;
}
.clearfix:after {
 clear: both;
 content: "";
 display: block;
}
/* 点滅 */
@keyframes blink {
 50% { opacity: 0; }
}
@-webkit-keyframes blink {
 50% { opacity: 0; }
}
.rpgboard {
  width: 100%;
  height: 120px;
  margin: 0 auto;
  font-size: 1.2rem;
  color: #FFF;
  border: 2px solid #000;
  border-radius: 5px;
  box-shadow: 0 0 0 4px #FFF inset;
  background-color: rgba(0,0,0,0.9);
  font-family: 'MS UI Gothic';
}
.rpg {
  width: 96%;
  margin: 2%;
  white-space: pre;
  overflow: hidden;
  font-family:'MS UI Gothic';
  -webkit-animation: ozDq 3s steps(70, end) ;
  /* プロパティは順に name, duration, timing-function です */
  }
.rpg:after {
  content: '▶';
  -webkit-animation: ozPulse 1s infinite ease;
  /* プロパティは順に name, duration, iteration-count, timing-function です */
}
@-webkit-keyframes ozDq { from { width: 0; } } /* 開始地点の指定 */
@-moz-keyframes ozDq { from { width: 0 } }
@-webkit-keyframes ozPulse { { /* 透明度の指定 */
  from { opacity: 1; }
  to { opacity: 0; }
}
@-moz-keyframes ozPulse {
from { opacity: 1; }
to { opacity: 0; }
}
@-webkit-keyframes blink {
 50% { opacity: 0; }
}
[[/module]]

