[[include :scp-wiki-cn:theme:bedrock]]
[[include :scp-wiki-cn:theme:lumina]]
[[module CSS]]
@import url(https://crimone.github.io/typefaces/fandol-fangsong/FandolFang-Regular.css);
@import url(https://fonts.bunny.net/css?family=long-cang:400);
@font-face { font-family:'LXGW WenKai GB'; 
  font-style: normal; font-weight: 400;font-display: swap;
  src: url('https://fastly.jsdelivr.net/gh/Etinjat/Fonts@main/FONT_LXGWWenKaiMonoGB-Regular.woff2') format('woff2');
}

/*==版式，修改自流明冲击==*/

:root {
    --base-font-size: 1.25rem;

    --basalt-sub-text-color: 195, 195, 195;
    --basalt-undertone: var(--basalt-bright-element-color);
    --basalt-overtone: 0, 0, 0;
    --basalt-bright-element-color: 249,243,192;
    --basalt-dark-element-color: 206,154,67;
    --link-color: var(--basalt-undertone);
    --link-visited-color: var(--basalt-dark-element-color);
    --header-title-color: 206,167,108;
    --header-subtitle-color: 226,207,130;
    --side-bar-button-width: 3.25rem;
    --side-bar-button-color: var(--basalt-undertone);

    --eye-color: 74,132,136;
    --tongue-color: 239,181,178;
    --lip-color: 201,112,120;
    --ear-color: 213,137,103;
    --nose-color: 206,173,150;

    --basalt-UI-opacity: .675;
    --basalt-UI-blur: .125rem;
    --base-header-height: min(calc(12.5rem + 13.25vmin), 19.5rem);
    --main-content-side-margin: calc(1.75rem + 1.75vw);

    --UI-font-primary: 'LXGW WenKai GB';
    --body-font-primary: 'FandolFang-Regular';
    --header-font: 'Long Cang';

    --header-title: "欢迎来到";
    --header-subtitle: "安布罗斯餐厅";
    --title-size: min(calc(1.375rem + 2vw), 3.75rem);
    --subtitle-size: min(calc(1.25rem + 2.95vw), 3.75rem);
    --lumina-top-bar-height: 3.25rem;
    --lumina-header-image: url(https://cdn.pixabay.com/photo/2024/04/08/21/09/design-8684495_640.png);
    --lumina-background: url(https://cdn.pixabay.com/photo/2020/01/09/14/17/abstract-4752871_640.jpg);
    --lumina-footer-image: url(https://cdn.pixabay.com/photo/2022/04/08/19/09/mandala-7120108_640.png);
}

#top-bar div[class*=top-bar]>ul>li>a {font-size: 1.19em;}

#header h1 a {
    gap: 1rem calc(var(--base-header-height)* .5);
    padding: .5rem;
}
#header h1 a::before {
    -webkit-mask-image: radial-gradient(circle, white 1%, transparent 120%);
    mask-image: radial-gradient(circle, white 1%, transparent 120%);
}

#header h1 a span::before {font-family: var(--body-font-primary);}

#header h1 a span::after {color: rgba(var(--header-subtitle-color), .89);}

:where(#page-content) :is(h1, h2, h3, h4, h5, h6)::before {border: double .3rem rgb(var(--header-title-color), .75);}

h1, h2, h3, h4, h5, h6 {color: rgb(var(--header-title-color));}

#breadcrumbs, .pseudocrumbs {
    --_gap: 1.375rem;
    padding: 0 var(--_gap);
    font-size: unset;
    font-weight:normal;
}

#footer::before {
    background-image: var(--lumina-footer-image);
    background-size: contain;
    background-repeat: no-repeat;
    -webkit-mask-image: radial-gradient(circle, white -12%, transparent 44%);
    mask-image: radial-gradient(circle, white -12%, transparent 44%);
}

.bibitems, .footnotes-footer {display: none;}
/* 一些小把戏 */
 /*半透明评分*/
div.page-rate-widget-box, div.rate-box-with-credit-button {background-color: transparent;}

#page-content .page-rate-widget-box, #page-info {
    opacity: 0.5;
    transition: opacity 0.3s ease;
}

#page-content .page-rate-widget-box:hover, #page-info:hover {
    opacity: 1;
}

div.page-rate-widget-box span.btn>a, div.rate-box-with-credit-button .creditButton>p>a {color: white !important;}

 /*悬浮切换*/
.hover-text {
    position: relative;
    display: inline-block;
}

.hover-text .original-text,
.hover-text .hovered-text {
    transition: opacity 0.3s ease;
}

.hover-text .hovered-text {
    opacity: 0;
    position: absolute;
    top: 0;
    left: 0;
}

.hover-text:hover .original-text {
    opacity: 0;
}

.hover-text:hover .hovered-text {
    opacity: 1;
}

/*反向面包屑*/
#breadcrumbs-right, .pseudocrumbs-right {
    --_gap: 1.375rem;
    padding: 0 var(--_gap);
    font-size: unset;
    font-weight:normal;
    position: relative;
    font-family: var(--UI-font);
    width: max-content;
    gap: var(--_gap);
    margin-inline-end: var(--main-content-side-margin);
    box-sizing: border-box;
    max-width: calc(100% - var(--main-content-side-margin)*2)!important;
    margin-inline-start: auto;
}
:is(#breadcrumbs-right, .pseudocrumbs-right)::before {
    content: "";
    position: absolute;
    inset: 0;
    background-color: rgb(var(--basalt-secondary-color));
    -webkit-backdrop-filter: blur(var(--basalt-UI-blur));
    backdrop-filter: blur(var(--basalt-UI-blur));
    z-index: -1;
}
:is(#breadcrumbs-right, .pseudocrumbs-right)::after {
    content: "";
    top: 50%;
    position: absolute;
    inset-inline-start: 100%;
    display: block;
    background-color: rgb(var(--basalt-main-text-color), .575);
    width: var(--main-content-side-margin);
    height: .05rem;
}
:is(#breadcrumbs-right, .pseudocrumbs-right) a {
    display: block;
    position: relative;
    text-decoration: none;
}
:is(#breadcrumbs-right, .pseudocrumbs-right) a::before {
    content: "";
    position: absolute;
    width: 0;
    height: .175rem;
    background-color: currentColor;
    inset-inline-end: 0;
    inset-block-end: calc(var(--_gap)*-1);
    transition: width .125s ease-out;
}
:is(#breadcrumbs-right, .pseudocrumbs-right) a:is(:hover, :focus)::before {
    width: 100%;
}

 /*抽卡*/
.card-container {
    position: relative;
    width: 220px;
    height: 320px;
    perspective: 1000px;
    margin: 0 auto;
}

.card {
    width: 220px;
    height: 320px;
    background-color: rgb(30, 30, 35);
    border: double 0.3rem rgb(var(--header-title-color), .75);
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
    transition: transform 0.6s ease, width 0.6s ease, height 0.6s ease;
    cursor: pointer;
    position: absolute;
    transform-style: preserve-3d;
    overflow: hidden;
}

.card-content {
    padding: 20px;
    text-align: center;
    backface-visibility: hidden;
}

.card1 {/*目*/
    transform: rotate(-60deg) translate(-60px, -66px);
    z-index: 1;
}

.card2 {/*耳*/
    transform: rotate(-30deg) translate(-32px, -20px);
    z-index: 2;
}

.card3 {/*鼻*/
    transform: rotate(0deg) translate(15px, 0px);
    z-index: 3;
}

.card4 {/*唇*/
    transform: rotate(30deg) translate(68px, -30px);
    z-index: 4;
}

.card5 {/*舌*/
    transform: rotate(60deg) translate(98px, -73px);
    z-index: 5;
}

.card:hover,
.card:focus-within,
.card:active {
    width: 400px;
    height: 400px;
    transform: scale(2) !important;
    z-index: 6;
    overflow: visible;
}

.card:hover .card-content,
.card:focus-within .card-content,
.card:active .card-content {
    padding: 0;
    transform: scale(0.8);
}

/* 移动端适配 */
@media (max-width: 768px) {
    :root {
        --base-font-size: 1.025rem;
    }

@media (max-width: 768px) {
    .card-container {
        width: 140px;
        height: 200px;
    }

    .card {
        width: 140px;
        height: 200px;
        transition: transform 0.6s ease, width 0.6s ease, height 0.6s ease;
    }

    .card:hover,
    .card:focus-within,
    .card:active {
        width: 240px;
        height: 240px;
        transform: scale(1.5) !important;
    }

    .card1 {/*目*/
        transform: rotate(-60deg) translate(-35px, -38px);
    }

    .card2 {/*舌*/
        transform: rotate(-30deg) translate(-18px, -12px);
    }

    .card3 {/*唇*/
        transform: rotate(0deg) translate(10px, 0px);
    }

    .card4 {/*耳*/
        transform: rotate(30deg) translate(42px, -20px);
    }

    .card5 {/*鼻*/
        transform: rotate(60deg) translate(60px, -43px);
    }
}
[[/module]]

