[[module CSS]]
@import url('https://fonts.googleapis.com/css2?family=Grenze+Gotisch&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Montserrat&display=swap');

#page-content h2 {
    text-align: center;
}

.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a,
.page-rate-widget-box .cancel a::after {
    color: #fff;
}

.blood-list {
    background: linear-gradient(135deg, #cb2f2f 0 19%, #621212);
    border-width: 0 6px;
    border-style: solid;
    border-color: #4a0707;
    color: white;
    padding: calc(1em + 10px) 1em;
    width: 90%;
    margin: auto;
    position: relative;
}

.blood-list::before,
.blood-list::after {
  content: "";
  position: absolute;
  pointer-events: none;
  top: -1rem; right: -1rem; bottom: -1rem; left: -1rem;
  background-image: url(https://scp-wiki.wdfiles.com/local--files/ambrose-transylvania/bloodsnip.png);
  background-position: 0 1rem;
  background-size: 100% 2rem;
  background-repeat: repeat-x;
}

.blood-list::after {
    transform: rotate(180deg);
}

.blood-name {
    font-family: 'Grenze Gotisch', cursive;
    font-size: 2.5em;
    text-shadow: 2px 2px 1px black;
}

.blood-name p {
    margin: 0;
}

.blood-desc {
    width: 83%;
    margin-left: auto;
    margin-right: 0;
    font-family: 'Montserrat', sans-serif;
    text-align: right;
}
