[[module CSS]]
/* MENU */

@import url('BLKCHCRY.otf');
@import url('BLKCHCRY.TTF');
@import url('BLKCHCRY.eot');
@import url('BLKCHCRY.woff');
@import url('BLKCHCRY.woff2');
@import url('ParaRubber-Medium.otf');
@import url('ParaRubber-Medium.ttf');
@import url('ParaRubber-Medium.eot');
@import url('ParaRubber-Medium.woff');
@import url('ParaRubber-Medium.woff2');

#page-content ol {
   list-style: none;
   margin: 0;
   padding: 0 1em 1em 1em;
}

#page-content ol li {
   text-align: justify;
}

#u-menu, #u-boissons {
   padding-bottom: 2em;
}

#u-ambrose-title {
   font-size: 5em;
   font-weight: bold;
   color: black;
   font-family: "BlackChancery", URW Chancery L, Times New Roman, cursive;
}

#u-ambrose-subtitle {
   font-size: 3em;
   font-weight: bold;
   color: black;
   font-family: "ParaRubber", 'Palatino Linotype', 'Book Antiqua', Palatino, serif;
}

@media only screen and (max-width: 995px) {
   #u-ambrose-title {
      font-size: 3.5em;
   }

   #u-ambrose-subtitle {
      font-size: 1.5em;
   }
}

.nombre-list {
   font-size: 1.5em;
}

#page-content h1 {
   font-size: 2em;
   font-weight: bold;
   color: black;
   font-family: "ParaRubber", 'Palatino Linotype', 'Book Antiqua', Palatino, serif;
   text-align: center;
}

#page-content h2 {
   font-size: 1.5em;
   font-weight: bold;
   color: black;
   font-family: "ParaRubber", 'Palatino Linotype', 'Book Antiqua', Palatino, serif;
   text-align: center;
}

.hovertip {
   border: 0.1em solid #b40431 !important;
   background-color: #f7fae6 !important;
   color: #000000;
   border-radius: 0.5em;
}

.main-title {
  color: #D2691E;
  font-weight: bold;
  border-bottom: 0.1em solid #FFD700;
  font-size: 2.5em;
  font-family: serif;
}

.menu-section {
  color: #D2691E;
  font-weight: bold;
  border-bottom: 0.1em solid #FFD700;
  font-size: 1.5em;
  font-family: serif;
}

.plat-title {
  font-weight: bold;
  font-family: Book Antiqua,Palatino,Palatino Linotype,Palatino LT STD,Georgia,serif;
  font-size: 110%;
}

.formule {
  font-weight: bold;
  font-family: Book Antiqua,Palatino,Palatino Linotype,Palatino LT STD,Georgia,serif;
}

@media only screen and (max-width: 995px) {
   #u-ambrose-title {
      font-size: 3.5em;
      line-height: 120%;
   }

   #u-ambrose-subtitle {
      font-size: 1.5em;
   }
}

/* COMMENTAIRES */

#page-content div.com {
     font-family: "Trip Sans", Arial, sans-serif !important;
     border-bottom: 1px solid #8e9297;
     color: #72767d;
     padding-bottom: 2.5em;
     margin: 1em 3em;
}

#page-content div.com h2 {
     font-size: large;
     font-weight: normal;
     color: #72767d;
     font-family: "Trip Sans", Arial, sans-serif !important;
     text-align: left;
}

/* TABS */

.yui-navset .yui-content {
    background-color: inherit;
}

.yui-navset .yui-nav a,.yui-navset .yui-navset-top .yui-nav a {
    background-color: inherit;
    background-image: inherit;
}

.yui-navset .yui-nav .selected a,.yui-navset .yui-nav .selected a:focus,/* no focus effect for selected */.yui-navset .yui-nav .selected a:hover {
    background: inherit;
    color: inherit;
}

.yui-navset .yui-nav a:hover,.yui-navset .yui-nav a:focus {
    background: inherit;
    text-decoration: inherit;
}

.yui-navset .yui-nav,.yui-navset .yui-navset-top .yui-nav,.yui-navset .yui-nav,.yui-navset .yui-navset-top .yui-nav {
    border-color: inherit;
}

.yui-navset li {
    line-height: inherit;
}

.yui-navset * {
    transition: background-color 80ms cubic-bezier(0.4,0.0,0.2,1),color 80ms cubic-bezier(0.4,0.0,0.2,1);
}

.yui-navset .yui-nav,.yui-navset .yui-navset-top .yui-nav {
    zoom: unset;
    display: flex;
    flex-wrap: wrap;
    border-color: black;
    border-style: double;
    width: calc(100% - 0.125rem);
    margin: 0 auto;
    border: 0;
}

.yui-navset .yui-nav a,.yui-navset .yui-navset-top .yui-nav a {
    zoom: unset;
    background-image: none;
    background-color: white;
    border: unset;
    color: black;
}

.yui-navset .yui-nav a:hover,.yui-navset .yui-nav a:focus {
    zoom: unset;
    background-color: black;
    color: white;
}

.yui-navset .yui-nav li,.yui-navset .yui-navset-top .yui-nav li {
    background-color: #f4f4f4;
    max-width: 100%;
    padding: 0;
    margin: 0;
    zoom: unset;
    display: flex;
    position: relative;
    flex-grow: 2;
    border-color: black;
    border-style: double double none double;
}

.yui-navset .yui-nav li a,.yui-navset-top .yui-nav li a,.yui-navset-bottom .yui-nav li a {
    display: flex;
    vertical-align: bottom;
    zoom: unset;
    width: 100%;
    align-items: center;
    justify-content: center;
}

.yui-navset .yui-nav a em,.yui-navset .yui-navset-top .yui-nav a em {
    padding: .35em .75em;
    white-space: nowrap;
    text-overflow: ellipsis;
    overflow: hidden;
}

.yui-navset .yui-nav li em {
    border: unset !important;
}

.yui-navset .yui-nav .selected,.yui-navset .yui-navset-top .yui-nav .selected {
    padding: 0;
    margin: 0;
    flex-grow: 2;
    background-color: black;
}

.yui-navset .yui-nav .selected a {
    color: #fcfcfc !important;
    width: 100%;
    background-image: none;
}

.yui-navset .yui-nav .selected a,.yui-navset .yui-nav .selected a em {
    border-style: double double none double;
    border-color: black;
}

.yui-navset .yui-nav .selected a:focus,.yui-navset .yui-nav .selected a:active {
    background-color: black;
    color: white;
}

.yui-navset .yui-nav .selected a:hover {
    cursor: default;
}

.yui-navset .yui-content {
    background-color: white;
    padding: auto 20px auto 20px;
    text-align: justify;
}

.yui-navset .yui-content,.yui-navset .yui-navset-top .yui-content {
    border-color: black;
    border-width: 5px;  
    border-style: double;
}

.yui-navset-left .yui-content {
    border-left-color:#f4f4f4;
}
[[/module]]

