[[module CSS]]
/* ==== FONTS ==== */

 @import url(https://fonts.googleapis.com/css2?family=Texturina:opsz@12..72&display=swap);

/* == COMMON == */

 :root {
	--lib-font: "Texturina", "Optima", "Segoe", "Segoe UI", "Candara", "Calibri", "Arial", sans-serif;
}

/* == ALL CHAPTERS == */

 .lite-heading.extra-wide {
	position: relative;
	left: calc(-33vw + 50%);
	width: 66vw;
	max-width: 66vw;

	background:
	   linear-gradient(
		to right,
		transparent,
		var(--fgColor) 10% 90%,
		transparent);
}

 .lite-heading.extra-wide hr {
	height: .3rem;

	background:
	   linear-gradient(
		to right,
		transparent,
		var(--accentColor) 10% 90%,
		transparent);
	border: 0;
}

 .lite-heading.extra-wide h1 {
	background:
	   -webkit-linear-gradient(
		var(--accentColor) 55%,
		transparent 85%);
	-webkit-background-clip: text;

	-webkit-text-fill-color: transparent;
	font-size: 2.95rem;
	letter-spacing: .4rem
}

 .lite-heading.extra-wide h3 {
	margin-bottom: .7em;

	font-size: 1.95rem;
	letter-spacing: .2rem
}

 @media (max-width: 767.9px) {
	 .lite-heading.extra-wide {
		left: unset;
		width: unset;
		max-width: unset;
	}
}

 @media (max-width: 575.9px) {
	 .lite-heading.extra-wide h1 {
		background:
		   -webkit-linear-gradient(
			var(--accentColor) 25%,
			transparent 95%);
		-webkit-background-clip: text;
	}
}

 h2, h4 {
	color: var(--textColorMid);
	filter: none;
}

 .scene-break.break-hydra {
	filter: invert(77%) sepia(24%) saturate(1063%) hue-rotate(78deg) brightness(87%) contrast(88%);
}

/* == EARTHWORM SERIES NAVIGATOR == */
/**
 *	By Croquembouche
**/

 #page-content .earthworm a[href="/"] {
	color: var(--textColorIdle);
}

 #page-content .earthworm .hub {
	background: var(--accentColorLite);
	border-color: transparent;
}

 #page-content .earthworm .hub a {
	color: var(--keyColor);
}

/* == CHAPTER-SPECIFIC == */

 .fancyborder {
	padding: 2vw .4vw;
}

 .fancyborder p {
	color: hsl(44, 56%, 68%);

	font: 1.25rem var(--lib-font);
}

 .fancyborder ::selection {
	background: hsl(44, 56%, 68%);
}

 @media (min-width: 768px) {
	 .flourish {
		letter-spacing: 3.6rem;
	}
}

 .flourish {
	letter-spacing: 2.4rem;
}

 .baetyl,
 .baetyl div:first-child {
	--fgColor: hsla(135, 20%, 9%, .65);

	border-radius: 2rem;
}

 .baetyl.engraving {
	background: url(https://scp-wiki.wikidot.com/local--files/an-old-familiar-dream/glade.webp);
	box-shadow:
		inset 0 0 2rem .5rem var(--keyColor),
		inset 0 0 4rem 1rem var(--keyColor),
		inset 0 0 8rem 2rem var(--keyColor);
}

 .baetyl div:nth-child(1) {
	background: var(--fgColor);
}

 .baetyl div div:nth-child(1) {
	margin-bottom: -1.1875rem;
	padding: .53rem 1rem;
}

 .baetyl div br:nth-child(2) {
	display: none;
}

 .baetyl.engraving p {
	filter: opacity(calc(100% - 12%));
	font: 1.25rem var(--script-font);
	letter-spacing: .033rem;
}

 .lost {
	font-size: 1.55rem;
	font-weight: bold;
}

 @media (max-width: 767.9px) {
	 .fancyborder p,
	 .baetyl.engraving p {
		font-size: .95rem;
	}

	 .lost {
		font-size: 1.25rem;
	}
}
