[[module css]]
.gazou{
filter: brightness(70%);
transform: matrix3d(0.5041,0,0.71,-0.0014,0.00,0.71,0.00,0,-0.50,0,0.50,0,0,0,0,1);
-webkit-transform: matrix3d(0.5041,0,0.71,-0.0014,0.00,0.71,0.00,0,-0.50,0,0.50,0,0,0,0,1);
transform-origin: 150px 85px; 
position:absolute;
bottom :96px;
left:47px;
width:100px; 
height:100px;
border:solid 3px #8C5E00;
}
.sotowaku{
background:url(http://scp-jp-storage.wikidot.com/local--files/file:7664517-117-xh4d/01B798BE-8E3C-4F4E-A894-F3A457A01C67.jpeg);
background-size:100%;
position: absolute;
    top: 0;
    left: 0;
    bottom: 0;
    right: 0;
}
.wrapper {
z-index:3;
    position: relative;
    width: 350px;
overflow:hidden;
}
.wrapper:before {
    content:"";
    display: block;
    padding-top: 75%; /* 高さを幅の75%に固定 */
}
.noise:after {
  animation: noise 0.5s steps(10) infinite;
  background: url("http://scp-jp-storage.wikidot.com/local--files/file:7664517-129-vuf9/%E3%83%A9%E3%83%B3%E3%83%80%E3%83%A0%E3%83%8E%E3%82%A4%E3%82%BA.png");
  content: "";
  display: block;
  height: 300%;
  left: -100%;
  position: absolute;
  top: -100%;
  width: 300%;
  z-index: 2;
opacity:20%;
}

@keyframes noise {
  to {
    background-position: 0 0;
  }
  10% {
    background-position: -5% -10%;
  }
  20% {
    background-position: -15% 5%;
  }
  30% {
    background-position: 7% -25%;
  }
  40% {
    background-position: 20% 25%;
  }
  50% {
    background-position: -25% 10%;
  }
  60% {
    background-position: 15% 5%;
  }
  70% {
    background-position: 0 15%;
  }
  80% {
    background-position: 25% 35%;
  }
  90% {
    background-position: -10% 10%;
  }
}

[[/module]]"
