[[form]]
fields:
  content:
    type: wiki
    height: 10
    default: "**説明:** 説明\n**回収日:** 20██/██/██\n**回収場所:** 回収場所\n**現状:** 現状\n**追記:** 追記 -██研究員"
    join: true
  tf2:
    label: "移転用"
    type: select
    values:
      false: 使用しない
      true: 使用する
    default: false
  author:
    type: text
    hint: "※許可無く使用しないでください"
    match: /[^、-◯ぁ-ん]*/
    match-error: "無効な記号です"
    join: true
  redirect-b:
    type: hidden
    value: '[[module Redirect destination="http://scp-jp.wikidot.com/log-of-anomalous-items-jp'
  redirect-e:
    type: hidden
    value: '"]]'
