[[module css]]

@import url('https://fonts.googleapis.com/css2?family=Vesper+Libre&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Spectral:wght@300&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Oranienbaum&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Special+Elite&display=swap');

:root {
  --logo: url(https://scp-wiki.wdfiles.com/local--files/anomaly-306-the-silver-alder/HMFSCP2.png);
  --title: "Her Majesty's Foundation";
  --subtitle: "Dieu Et Sa Loi";
  --title-size: 1.75rem;
  --subtitle-size: 1.25rem;
}
:root {
    --basalt-overtone: 10, 10, 11;
    --basalt-undertone: var(--basalt-bright-element-color);
    --basalt-UI-dark-palette: 23, 33, 20;
    --basalt-background-color: 224, 213, 191;
    --basalt-bright-element-color: 34,49,29;
    --basalt-dark-element-color: 34,49,29;
    --basalt-primary-color: 255, 253, 231;
    --basalt-secondary-color: 255, 239, 203;
    --basalt-tertiary-color: 246, 227, 186;
    --basalt-main-text-color: 18, 9, 0;

    --image-caption-background-color: 255, 253, 231;

    --header-background-color: 48,69,41;
    --header-title-color: 255, 253, 208;
    --header-subtitle-color: 255, 253, 208;

    --top-bar-link-color: 255, 253, 208;
    --search-icon-color: 255, 253, 208;
    --side-bar-background-color: 63,90,54;
    --side-bar-heading-text-color: 255, 253, 208;
    --side-bar-link-color: 255, 253, 208;
    --scrollbar-thumb-color: var(--basalt-dark-element-color);
    --general-border-color: 156, 156, 156;

    --link-color: 137, 81, 37 !important;
    --link-visited-color: 84, 49, 22 !important;

    --document-background-color: 243,223,193;
}
:root {
    --header-font-primary: 'Spectral';
    --UI-font-primary: 'Vesper Libre';
    --body-font-primary: 'Vesper Libre';
    --mono-font-primary: 'Spectral';
    font-size: 110%;
}
div.papernote {
    background-color: rgb(243, 206, 161);
    color: rgb(50, 50, 50);
}
[[/module]]

[[module CSS]]
div.newspaper-border {
    border: 2px solid black;
}
div.newspaper-main {
    font-family: 'Oranienbaum', sans-serif;
    background-color: rgb(245, 242, 232);
    text-align: center;
}
div.newspaper-body {
    font-family: Oranienbaum;
    font-size: 0.8em;
    padding: 1em;
}
div.newspaper-body div.scp-image-block {
    margin: 0.5em 0 0 1em;
}
div.rejection {
    border: 4px solid #ff4040;
    border-radius: 30px;
    font-weight: bold;
    color: #ff4040;
    text-align: center;
    margin: 0.5em;
}
div.rejection-wrap {
     transform: rotate(-3.8deg);
     width: 35vw;
     margin-bottom: 1em;
     margin-left: 3em;
}
.twopanel {
    display: flex;
    flex-flow: row wrap;
    justify-content: space-evenly;
}
.bit {
    flex-basis: 48%;
}
@media only screen and (max-width: 768px) {
    .twopanel {flex-direction: row-reverse;}
    .bit {flex-basis: 100%;}
}
[[/module]]

