	text-align: center;
	padding: 0.75em;
	background-color: rgba(var(--swatch-menubg-medium-color),0.25);
	border-top: 0.125rem dashed rgb(var(--swatch-menubg-medium-dark-color));
	border-bottom: 0.125rem dashed rgb(var(--swatch-menubg-medium-dark-color));
