[[module CSS]]

@import url('https://fonts.googleapis.com/css2?family=Noto+Serif+SC:wght@500;600&display=swap');

/* 缩进 */

.no-suojin p{
   text-indent:0 !important; 
}

#page-content p{
   text-indent: 2em;
}

.hovertip,  .blockquote p, .no-indent p,
.scp-image-caption p, #u-in-phone p,
#page-content .footnotes-footer p,
#page-content span,
#page-content blockquote p,
#page-content a{
   text-indent: 0em !important;
}

/*-------------- 百科版式调整 ----------------*/
:root {
    --lgurl: url("http://scpsandboxcn.wikidot.com/local--files/image/Antimeme-by-Sunnyclockwork.png");
   --header-title: "SCP基金会";
        --header-subtitle:"黑条、删除、编辑";
}

/* 字体及字重 */
div#extra-div-1:before,div#extra-div-1:after, #page-title,
#page-content h1, #page-content h2, #page-content h3, #page-content h4, #page-content h5, #page-content h6 {
    font-family: 'linux_libertinesemibold', 'Georgia', '华文中宋', 'Noto Serif SC', serif;
    font-weight: 500;
}

#side-bar ,#top-bar, .page-tags,
.hovertip, #page-content {
   font-family: 'Noto Sans SC', '微软雅黑','等线';
}

.sub-tt {
    font-family:  'Noto Serif SC', serif;
    font-weight: 500;
}

@media (max-width: 600px) {


div#extra-div-1:before,div#extra-div-1:after, #page-title,
#page-content h1, #page-content h2, #page-content h3, #page-content h4, #page-content h5, #page-content h6 {
    font-family:   'Noto Serif SC', serif !important; 
    font-weight: 600;
}
}

/**/

#main-content {
    width: calc(100vw - 41rem);
   margin-left: 22rem;
}

#top-bar, .page-tags,
.hovertip, #page-content {
  font-size: 120%;
}

#top-bar{
      left:-8.5rem;
}

.side-block:first-child{
   margin-top: 1rem;
}

#side-bar {
   left: 4rem;
   font-size: 120%;
line-height: 1.5rem;
}

#page-title{
   border-bottom: none;
   text-align:center;
     font-size: 300%;
padding: 1rem;
}

div#extra-div-1 {
    background-position: 6rem 50%;
}

@media (max-width: 1300px) {
#main-content {
    width: 67.5vw;
    margin-left: 25vw;
   margin-right: 5vw;

}
      #top-bar{
      left:auto;
}
   #side-bar {
      left:1vw;
      width: 20vw;
   }
       #top-bar {
          left: 0;
          right: -6rem;
}
      div#extra-div-1 {
        background-position: 5% 0%;
   }
}

@media (max-width: 740px) {
 #page-content {
      margin-left: 0rem;
}
   #top-bar, .page-tags,
.hovertip, #page-content {
  font-size: 100%;
}
    #top-bar {
  font-size:84%;
right: inherit;
}  

   #main-content {
    width: 100vw;
    margin:0 auto;
}
   #top-bar{
      left:auto;
}
   #side-bar {
   left: -25em;
   font-size: 100%;
      width: auto !important;
max-width:50vw;
}
   #page-title{
     font-size: auto;
     padding: 1rem;
}
   div#extra-div-1 {
        background-position: 50% 50%;
   }
}

/* 小标题 */
/* 小标题 */
#page-content h2,
.sub-tt {
border-left: 10px solid #333;
   padding: 0;
   border-bottom: 0;
}

#page-content h2,
.sub-tt p{
   text-indent: 0.5em !important;  
   font-size:140%;
   letter-spacing: 0.2rem;
}

#page-content h3 {
   border-bottom: none;
   padding-bottom: 0;
}

#page-content h3:before {
content: '◎';
   padding: 0 0.5rem;
}

/* 文末表格 */
.articlecolumn{
   text-align:left;
   padding: 0 0.5em;
}

.titlecolumn{
min-width: 5rem;
width: 20%;

}

/* 其他 */

.no-suojin p{
text-indent:0 !important;

}

.li-height li{
    margin-top: 0.5rem;
}


/*----------------- 调整----------------------*/


/* 目录 */
#u-float-memu  #toc-list{
   margin: 0 0 0 1rem;
   line-height: 1.8rem;
   padding-right: 1rem;
}

#u-float-memu #toc-list a::before ,
#u-float-memu #toc-list div:hover:before {
  left:1.8rem;
}

#u-float-memu #toc-list a{
font-size: 125%;
}

#u-float-memu .fm-goto {
    margin-left: -1rem;
}

/* 版头标题 */
div#extra-div-1::before {
    padding-left: 5rem;
}

@media (max-width: 740px) {
:root {
   --header-title: "\00A0SCP基金会";
}
}

#page-content #toc {
  background-color: unset;
  border: unset;
}

#page-content #toc-action-bar, #toc .title{
 display: none;
}



