[[module CSS]]
/* タブデザイン */
.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
  zoom: unset;
  display: flex;
  flex-wrap: wrap;
  box-shadow: 0 .3125rem 0 0 #112211;
  box-sizing: border-box;
  border: solid 2px #112211;
  font-size: 120%;
}

/* 通常時のタブ */
.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
  zoom: unset;
  border: unset;
  background-image: none;
  transition: color 0.25s, background-color 0.25s;
  color: black;
  background-color: #b2d2b4;
}

/* hover時のタブ */
.yui-navset .yui-nav a:focus,
.yui-navset .yui-nav a:hover {
  zoom: unset;
  background: none;
  background-color: #ffdb5a;
  color: white;
}

.yui-navset .yui-nav li,
.yui-navset .yui-navset-top .yui-nav li {
  zoom: unset;
  display: flex;
  position: relative;
  flex-grow: 2;
  margin: 0;
  box-shadow: 0 0 0 .0625rem #112211;
  border-color: rgba(0, 0, 0, 0);
  background-color: #f4f4f4;
  background: none;
  padding: 0;
  max-width: 100%
}

.yui-navset-bottom .yui-nav li a,
.yui-navset-top .yui-nav li a,
.yui-navset .yui-nav li a {
  zoom: unset;
  display: flex;
  align-items: center;
  justify-content: center;
  vertical-align: bottom;
  width: 100%
}

.yui-navset .yui-nav a em,
.yui-navset .yui-navset-top .yui-nav a em {
  -o-text-overflow: ellipsis;
  padding: .35em .85em;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap
}

.yui-navset .yui-nav li em {
  border: unset !important
}

.yui-navset .yui-nav .selected,
.yui-navset .yui-navset-top .yui-nav .selected {
  flex-grow: 2;
  margin: 0;
  background-color: #e4b41c;
  padding: 0
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a em {
  border: rgba(0, 0, 0, 0)
}

.yui-navset .yui-nav .selected a {
  background: none;
  background-image: none;
  width: 100%;
  color: #fcfcfc !important;
}

.yui-navset .yui-nav .selected a:active,
.yui-navset .yui-nav .selected a:focus {
  background-color: gray;
  color: gainsboro;
}

.yui-navset .yui-nav .selected a:hover,
.yui-navset .yui-nav .selected a:focus {
  cursor: default;
  background: none;
  background-image: blue:
}

.yui-navset .yui-content {
  background-color: #ddeedd;
  padding: 15px 25px 15px;
}

.yui-navset .yui-content,
.yui-navset .yui-navset-top .yui-content {
  border: solid 2px;
  border-top: none;
  border-color: #112211;
}

.yui-navset-left .yui-content {
  border-left-color: #f4f4f4;
}

@media (max-width: 480px) {
  .yui-navset .yui-content {
    padding: 15px 17px 15px;
  }
}
[[/module]]

[[module CSS]]
/* 蛇の手テーマ */
@import url(https://scp-jp.github.io/files/theme/wanderers/style0.css);

#page-content .rate-box-with-credit-button .creditButton {
    background-color: #006c00;
    border: 1px solid #006c00;
}

#page-content .rate-box-with-credit-button .creditButton:hover {
    background-color: #008f00;
    border: 1px solid #008f00;
}

#page-content .rate-box-with-credit-button .creditButton a:hover {
    color: white;
}
[[/module]]

[[module CSS]]
/* その他ブロック */
#page-title {
  display: none;
}

.fake-title {
  font-size: 200%;
  color: #044d00;
  padding: 0 0 0.25em;
  margin: 0 0 0.6em;
  border-bottom: 1px solid #bbb;
  font-weight: normal;
}

.fake-title p {
  padding: 0;
  margin: 0;
}

.ao {
  display: block;
  background:#38b48b;
  font-size: 10pt;
  width: 65%;
  margin: 20px auto 20px 0px;
  padding: 2px 25px;
}

.newbie {
  display: block;
  background:#f8e58c;
  font-size: 10pt;
  width: 65%;
  margin: 20px 0px 20px auto;
  padding: 2px 25px;
}

.box {
  border: solid 1px #112211;
  background:#EEF6EE;
  padding: 20px;
  margin: 5px 0px 3px 0px;
}

.oldbox {
  border: solid 2px #004F00;
  background: #FFFDD9;
  padding: 20px;
  margin: 5px 0px 3px 0px;
}

hr {
  height: .125rem;
  border: 0;
  border-top: .063rem solid transparent;
  background-color: rgba(var(--bright-accent));
  background: url(http://wanderers-library.wikidot.com/local--files/component:dustjacket-theme/goldfoil.png);
  background-size: cover;
}

@media (max-width: 480px) {
  .ao, .newbie {
    font-size: 12px;
    padding: 2px 20px;
  }

  .box, .oldbox {
    padding: 9px 12px;
    margin: 5px 0px 3px 0px;
  }
}

.profile {
    width: calc(100%-120px);
    vertical-align: middle;
    padding-left: 10px;
}

.emblem {
    width: 90px;
    text-align: left;
    padding: 0 20px 0 10px;
    vertical-align: middle;
}

@media (max-width: 480px) {
    .profile {
        width: calc(100%-100px);
    }

    .emblem {
        width: 70px;
    }
}

[[/module]]

