[[module css]]
body {
	background-image: url(http://themes.wdfiles.com/local--files/underwater/bg.jpg);
	background-position: top center;
	background-repeat: no-repeat;
	background-color: #002C2C;
	color: white;

}


/* 1st MENU ELEMENT */

/* Background color */

#top-bar {
	bottom: 7px;
	left: 0px;
	position: absolute;
	width: 900px;
}

#top-bar li {
	background-color: transparent;
	-moz-border-radius-topleft: 10px;
	-moz-border-radius-topright: 10px;
}

/* Font color and decoration */
#top-bar li a {
	color: #5eb0db;
	text-decoration: none;
}

/* Background color on hover */
#top-bar a:hover{
	background-color: transparent; 
	-moz-border-radius-topleft: 10px;
	-moz-border-radius-topright: 10px;
}

/* Font color on hover*/
#top-bar li a:hover {
	color: white;
	text-decoration: underline;
}
/* ---------------------------------- */

/* 2nd MENU ELEMENT */
#top-bar ul li ul {
	background-color: black;
	opacity: 0.5;
	border: 0px;
}

#top-bar ul li ul a {
	background-color: black;
	-moz-border-radius: 0px;
}

#top-bar ul li ul a:hover {
	background-color: white; 
	color: black;
	-moz-border-radius: 0px;
}


div#container-wrap {
    background-image: url('');
}

#header {
    background-image: url("");
    background-size: 100px 100px;
}

#header h1 span {
    color: transparent;
    text-shadow: none;
 }

#header h1 a::before {
    content: "상어죽빵센터";
    color:#5eb0db;
}

#header h2 span {
    color: transparent;
    text-shadow: none;
 }

#header h2 span::before {
    content: "Shark Punching Center";
    color:#5eb0db;
}

#page-title {
   color: #5eb0db;
}


a {
	color:  #5eb0db;
}

a:hover {
	color: #cde7f4;
}

a:visited {
	color:  #5eb0db;
}

a.newpage {
	color:  #5eb0db;
}

a.newpage:hover {
	color: #cde7f4;
	text-decoration: underline;
}


/* -------------- SIDE-BAR -------------- */
#side-bar {
    clear: none;
    float: right;
    top: .5em;
    left: 2em;
    width: 17em;
    padding: 0;
    display: block;
    overscroll-behavior: none;
	background-image: url(http://themes.wdfiles.com/local--files/underwater/rightbg.jpg);
	background-repeat: no-repeat;

}

#interwiki .side-block {
    margin: 5px;
}

#side-bar .side-block,
#interwiki .side-block {
    padding: 10px;
    border:1px solid #234848;
    background: #0A3838;
    margin-bottom: 15px;
}

#side-bar .side-block.media {
    background: #e5e5ff;
}

#side-bar .side-block.resources {
    background: #e7f7e9; /* Changed from #fff0f0 */
}

#side-bar .side-area {
    padding: 10px;
}

#side-bar .heading,
#interwiki .heading {
    color: white;
    border-bottom: solid 1px #234848;
    padding-left: 15px;
    margin-top: 10px;
    margin-bottom: 5px;
    font-size: 8pt;
    font-weight: bold;
}

#side-bar p,
#interwiki p {
    margin: 0;
}

#side-bar .menu-item,
#interwiki .menu-item {
    margin: 2px 0;
}

#side-bar .menu-item.small {
    font-size: 90%; /* For sidebar item with smaller text, like SCP series links */
}

#side-bar .menu-item img,
#interwiki .menu-item img {
    width: 13px;
    height: 13px;
    border: 0;
    margin-right: 2px;
    position: relative;
    bottom: -2px;
}

#side-bar .menu-item a,
#interwiki .menu-item a {
    font-weight: bold;
    color:#5eb0db;
}

#side-bar .menu-item.inactive img {
    opacity: .25;
}

#side-bar .menu-item.inactive a {
    color: #5eb0db;
}

#side-bar .menu-item .sub-text {
    font-size: 80%;
    color: #666;
}

#side-bar .collapsible-block-link {
    margin-left: 0;
    font-weight: bold;
    color:#5eb0db;
}

#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #234848;
}

#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    margin-top: 10px;
    margin-bottom: 5px;
    margin-left: 15px;
    font-size: 8pt;
    color: #5eb0db;
}

#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    color: #cde7f4;
    text-decoration: none;
}

#side-bar ul {
    list-style-type: none;
    padding: 0 5px;
}

/* side bar mobile query on their own */
@media (max-width: 767px) {
    #top-bar .open-menu a {
        position: fixed;
        top: .5em;
        left: .5em;
        z-index: 15;
        font-family: 'Nanum Gothic', sans-serif;
        font-size: 30px;
        font-weight: 700;
        width: 30px;
        height: 30px;
        line-height: .9em;
        text-align: center;
        border: .2em solid #234848;
        background-color: #0A3838;
        color: #888;
    }

    #top-bar .open-menu a:hover {
        text-decoration: none;
        -webkit-box-shadow: 0 0 20px 3px rgba(153, 153, 153, 1);
        box-shadow: 0 0 20px 3px rgba(153, 153, 153, 1);
    }

    #side-bar {
        display: block;
        position: fixed;
        top: 0;
        left: -25em;
        width: 17em;
        height: 100%;
        background-color: #0A3838;
        overflow-y: auto;
        z-index: 10;
        padding: 1em 1em 0;
        -webkit-transition: left .5s ease-in-out .1s;
        -o-transition: left .5s ease-in-out .1s;
        transition: left .5s ease-in-out .1s;
    }

    #side-bar::after {
        content: '';
        position: absolute;
        top: 0;
        width: 0;
        height: 100%;
        background-color: #0A3838;
    }

    #side-bar:target {
        display: block;
        left: 0;
        width: 17em;
        margin: 0;
        border: 1px solid #234848;
        z-index: 10;
    }

    #side-bar:target .close-menu {
        display: block;
        position: fixed;
        width: 100%;
        height: 100%;
        top: 0;
        left: 0;
        background: rgba(0, 0, 0, .3) 1px 1px repeat;
        z-index: -1;
    }
}
