[[module css]]

#page-content div:hover {
    margin: 50px 0px 50px 0px;
    transition: 2s;
    transition-delay: 1s;
}

#page-content a.library:hover {
    margin: 50px 0px 50px 0px;
    transition: 2s;
    transition-delay: 1s;
}

@media (max-width: 479px) {
    #page-content div:hover {
        margin: 50px 0px 50px 0px;
        transition: 0.7s;
        transition-delay: 0.3s;
