    margin: 0;
}

.info-block .panel-body {
    padding: 0;
}

.info-block .info-title {
    font-weight: bold;
    font-size: 115%;
    margin-bottom: 5px;
}

.info-block .info-subtitle {
    font-size: 80%;
    margin: 0 0 5px 1em;
