[[module CSS]]
@font-face {
    font-family: 'KIMM_Bold';
    src: url('https://cdn.jsdelivr.net/gh/projectnoonnu/noonfonts_2212@1.0/KIMM_Bold.woff2') format('woff2');
    font-weight: 700;
    font-style: normal;
}

div#container-wrap{background:none}
#header,#page-title,.ko-footer,#page-options-container,#footer,#license-area
{display:none !important}
#content-wrap{margin-top:0;min-height:0}
#content-wrap,#main-content,#page-content{position:static}

html, body {
  max-width: 100vw;
  overflow-x: hidden;
  min-height: 100vh;
}
body {
  scroll-behavior: smooth;
  -ms-overflow-style: none; 
  scrollbar-width: none;
}
body::-webkit-scrollbar {
  display: none;
}

.topper {
  display: flex;
  justify-content: space-between;
  align-items: center;
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  height: 64px;
  padding: 13px 85px 13px 35px;
  background: #FFF;
  z-index: 3;
}

.topper .topmenu {
  display: flex;
  justify-content: space-between;
  align-items: center;
  flex-wrap: wrap;
  width: 35vw;
}

.topper .scpf::before {
  background: url('http://scpko.wdfiles.com/local--files/thd-glasses-hub/scp_ko.png') center/contain no-repeat;
  width: 56px;
  height: 56px;
  content: "";
  display: block;
  position: absolute;
  margin-left: -65px;
}

.topper .scpf h1 {
  font-family: 'Sans Normalcy', san-serif;
  font-size: 50px;
  color: #333;
  margin: 0;
  white-space: nowrap;
}

.topper div h2 {
  font-size: 1.5em;
  text-align: center;
  white-space: nowrap;
}

.topper div h2 a {
  color: #333;
  transition: 0.2s;
}
.topper div h2 a:hover {
  color: #999;
  text-decoration: none;
  transition: 0.2s;
}

.page {
  position: absolute;
  left: 0;
  right: 0;
  z-index: 1;
}

.page a, .page a.newpage, .page a:visited {
  color: unset;
  text-decoration: none;
  transition: all 0.2s;
}

.page a:hover {
  filter: invert(1);
  transition: all 0.2s;
}

.wallpaper {
  position: absolute;
  top:0;
  bottom:0;
  width: 100%;
  animation: ease-in-out 18s infinite wp;
}

.wpbar {
  display: inline-block;
  width: 15%;
  height: 5px;
  background: #999;
  box-shadow: 2px 2px 0 #333;
  animation: ease-in-out 18s infinite wpb;
}

.stepflex p {
  flex: 1 1;
}

.step {
  display: block;
  position: relative;
  border: 3px solid #999;
  border-radius: 0 10px 0 10px;
  width: 100%;
  height: 200px;
  font-size: 20px;
  line-height: 1.4;
  font-weight: 700;
  font-family: 'Sans Normalcy', 'KIMM_Bold', san-serif;
  text-align: center;
  word-break: keep-all;
  padding: 0 5px;
  box-sizing: border-box;
}

.step:not(.last)::after {
  content: "》";
  position: absolute;
  top: 50%;
  right: -32px;
  z-index: 5;
  font-size: 50px;
  transform: translate(50%, -50%);
}

.open-menu a {
  position: fixed;
  top: 0.5em;
  right: 0.5em;
  z-index: 5;
  font-family: 'Nanum Gothic', san-serif;
  font-size: 38px;
  font-weight: 700;
  width: 36px;
  height: 36px;
  line-height: 0.9em;
  text-align: center;
  border: 0.2em solid #888;
  background-color: #fff;
  color: #888;
  pointer-events: auto;
}

.creditRate {
  float: none;
  margin-top: 10px;
  margin-right: 0px;
  margin-bottom: 10px;
}

.rate-box-with-credit-button, .page-rate-widget-box, .page-rate-widget-box .rate-points, .page-rate-widget-box .rateup, .page-rate-widget-box .ratedown, .page-rate-widget-box .cancel {
  background-color: #333;
  border: none;
  color: #FFF;
  box-shadow: none;
  border-radius: 0;
}

.creditButton p a {
  border-color: #FFF;
}

.page-rate-widget-box .rateup a, .page-rate-widget-box .ratedown a, .page-rate-widget-box .cancel a {
  background-color: #333;
  color: #FFF;
}

.page-rate-widget-box .rateup a:hover, .page-rate-widget-box .ratedown a:hover, .page-rate-widget-box .cancel a:hover {
  background-color: transparent ;
  color: #888;
}

.modalbox {
  color: #333 !important;
}

@media (max-height: 500px) {
  #u-p3 {
    top: unset !important;
    bottom: calc(80px - 80vh);
  }
}

@media (max-width: 1000px) {
  .wide { display: none; }
  .topper { padding: 13px 80px 13px 20px; }
  .topper div h2 { font-size: 1.2em; }
}

@media (max-width: 767px) {
  .topper div h2 { width: 45%; }
  .creditRate { margin-bottom: -10px; }
}

@media (max-width: 479px) {
  .topper .topmenu { display: none; }
}

@media all {
  #side-bar {
    left: unset;
    right: -18rem;
    background-color: #FFF;
    transition: right 0.4s ease-in-out;
    -ms-overflow-style: none; 
    scrollbar-width: none;
  }
  #side-bar::-webkit-scrollbar {
    display: none;
  }

  #side-bar:target {
    left: unset;
    right: 0;
    transition: right 0.4s ease-in-out;
  }
  #side-bar:focus-within {
    left: unset;
    right: 0;
  }
  #side-bar:target .close-menu {
    opacity: 0.3;
    background-color: #000;
    left: unset;
    right: 0;
    margin-left: 0;
  }
}

@keyframes wp {
  0% { left: 100vw; opacity: 0; }
  3% { left: 0; opacity: 1; }
  33.3% { left: 0; opacity: 1; }
  36.6% { left: -100vw; opacity: 0; }
  100% { left: 100vw; opacity: 0; }
}

@keyframes wpb {
  0% { background: #999; }
  3% { background: #0DF; }
  33.3% { background: #0DF; }
  36.6% { background: #999; }
  100% { background: #999; }
}

[[/module]]
