[[module CSS]]

/* Pizza Theme */
/* by syuzhet */

/* Tech team member, you are forbidden from scrolling any further. Turn off your computer NOW! (Bene Gesserit voice) */

:root {
  --header-background-color: 215, 29, 48;
  --base-header-height: 5rem;

  --top-bar-link-color: 255, 255, 255;
  --top-bar-dropdown-link-color: 0, 0, 0;
  --header-UI-font-size: 1.2rem;
  --header-UI-dropdown-font-size: 1rem;
}

#container #header {
    box-shadow: unset;
}

#header {
  position: relative;
  top: 2rem;
  width: 95%;
  margin: auto;
  padding: 0 2rem;
  border-radius: 2rem;
  filter: drop-shadow(0px 5px 5px rgba(0,0,0,0.3));
}

#header h1 a span {
  display: none;
}

#container #header h1 a {
  padding: 0;
}

#container #header h1 a::before {
  content:'';
  display:block;
  width: 6rem;
  max-width: 6rem;
  margin: 0 2rem;
  background-image: url(https://scp-wiki.wdfiles.com/local--files/april-fools-2024/pizza%20logo%202.png);
  background-position:center;
  background-repeat:no-repeat
}

/* Widebox */

#page-content > p:nth-child(-n+4) {
    display: none;
}

.widebox {
    position: relative;
    left: calc(-50dvw + 50%);
    width: 100dvw !important;
    max-width: 100dvw !important;
}

.banner {
    height: calc(85dvh);
    background-image: url(https://scp-wiki.wdfiles.com/local--files/april-fools-2024/pizza.jpg);
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center;
}

.banner .bannertext {
    color: black;
    position: absolute;
    font-size: 2rem;
    text-align: center;
    left: 12dvw;
    top: 20dvh;
    width: 35dvw;
    font-weight: 600;
}

.banner .bannertext p {
     line-height: 2rem;
}

.banner br {
  content: "";
  margin: 1rem;
  display: block;
  font-size: 24%;
}

.banner span {
    color: #DA2D3F;
    font-size: 5vw;
    white-space: nowrap;
    font-weight: 700;
}
@media screen and (min-width: 1920px) {
  .banner span {
     font-size: 6rem;
  }
}



.banner .orderbox {
     background: #DA2D3F;
     font-weight: 600;
     color: white;
     width: 50%;
     border-radius: 2rem;
     margin: auto;
     padding: 1px;
     user-select: none;
}

.banner .orderbox:active {
     animation-name: linkshaker;
    animation-duration: 0.2s;
}

@keyframes linkshaker {
    0% {
        transform: translateX(10px) translateY(10px);
    }
    50% {
        transform: translateX(-10px) translateY(-10px);
    }
    100% {
        transform: translateX(0px) translateY(0px);
    }
}

.banner .orderbox p {
     font-size: 1.5rem;
     line-height: 1rem;
}

/* Features */

p #u-featurebigtext{
     display: block;
     text-align: center;
     font-weight: 700;
     font-size: 3rem;
}

.featuresbox {
     display: flex;     
     align-items: center;
     justify-content: center;
}

.featuresbox .feature {
     position: relative;
     display: block;
     background: red;
     height: 20rem;
     width: 40rem;
     margin: 0rem 1rem;
     border-radius: 2rem;
     overflow: hidden;
}

.featuresbox .feature img {
     height: 100%;
     width: 100%;
     object-fit: cover;
}

.featuretext {
     position: absolute;
     padding: 0rem 2rem;
     color: white;
     font-size: 2rem;
     font-weight: 700;
     bottom: 0;
     pointer-events: none;
}

.feature:before {
    content: "";
    position: absolute;
    background: linear-gradient(0deg, rgba(0,0,0,1) 0%, rgba(0,0,0,0) 35%);
    height: 100%;
    width: 100%;
    z-index: 1;
    opacity: 0.25;
    transition: 0.5s;
    pointer-events: none;
}

.feature:hover:before {
    opacity: 0.75;
}

.featuretext {
     z-index: 1;
}

.featuretext p {
     line-height: 3rem;
     margin: 1rem 0;
}

.featuretext .featureorderbox {
     color: black;
     font-size: 1rem;
     background: white;
     padding: 0.5rem 2rem;
     border-radius: 1rem;
}

/* Piece of shit sidebar */

#top-bar div.mobile-top-bar>.open-menu {
     visibility:visible;
     opacity: 0.6;
     transition: opacity 0.2s;
}

#top-bar div.mobile-top-bar>.open-menu:hover {
    opacity: 1;
}

#side-bar {
     margin-top:0;
     height:100%
}

#side-bar, #side-bar:is(:hover, :focus-within):not(:target) {
     left:calc(var(--side-bar-width) * -1)
}

#side-bar::after, #side-bar::before {
     display:none
}

#side-bar:target {
     left:0;
     width:var(--side-bar-width);
     z-index:10
}

#side-bar a.close-menu, #side-bar:target a.close-menu {
     display:block;
     pointer-events:none;
     position:fixed;
     top:0;
     right:0;
     left:auto;
     width:100%;
     min-width:3.75rem;
     height:100%;
     margin-left:0;
     opacity:0;
     z-index:-1;
     transition:width var(--side-bar-transition),opacity var(--side-bar-transition)   
}

#side-bar:target a.close-menu {
     width:calc(100% - var(--side-bar-width));
     pointer-events:auto;
     opacity:1;
}

/* Mobile */

@media only screen and (max-width: 1020px) {

#header {
  top: revert;
  width: 100%;
  margin: revert;
  padding: revert;
  border-radius: revert;
}
.banner {
    background-image: url(https://scp-wiki.wdfiles.com/local--files/april-fools-2024/pizza2.jpg);
}
.banner .bannertext {
    font-size: 1.5rem;
    left: 0;
    right: 0;
    margin: auto;
    width: 90%;
}

.banner .bannertext p {
     line-height: 2rem;
}

.banner br {
  margin: 0.5rem;
}

.banner span {
    font-size:4rem;
}

p #u-featurebigtext{
     font-size: 2.5rem;
}

.featuresbox {
     flex-direction: column;
}
.featuresbox .feature {
     height: 15rem;
     margin: 0.25rem 1rem;
     border-radius: revert;
}

}


[[/module]]

