[[module CSS]]
.archived-top-box {
  background-color: #e3e3e3;
  border: 2px solid #a1a1a1;
  border-radius: 2px;
  width: 50%;
  margin: 0 auto 2em;
  text-align: center;
  padding: 10px;
}

.archived-top-box h1 {
  color: #333;
}

@media (max-width: 479px) {
  .archived-top-box {
    width: auto;
    margin: 0 0 2em;
  }
}
@media (min-width: 480px) and (max-width: 580px) {
  .archived-top-box {
    width: auto;
    margin: 0 0.5em 2em;
  }
}
[[/module]]

