    background: url(http://scp-jp.wikidot.com/local--files/r-sinano-chuo-co-jp-hub/%E4%BF%A1%E6%BF%83%E4%B8%AD%E5%A4%AE%E3%83%98%E3%83%83%E3%83%80%E3%83%BC.png) top left repeat-x;
}
