[[html]]
<center>
<head>
<style>
#aa { 
    border-style: solid;
			border-color: black;
			border-width: thin;
			background-color: white;
			height: 35px;
			width: 200px;
}

</style>
<script language="javascript">

function LogIn(){
loggedin=false;
password="";
password=prompt("密碼：","");
password=password.toLowerCase();

// Add password-->
if (password=="微微风簇浪，散作满河星") {
loggedin=true;
window.open('/area-cn-05');
}

// Add password-->
if (password=="情人怨遥夜，竟夕起相思") {
loggedin=true;
window.open('/hidden data');
}

// Add password-->
if (password=="随机页") {
loggedin=true;
window.open('/dududu');
}

// If login incorrect -->
if (loggedin==false) {
alert("请离开访问界面并将双手放在头上。您没有权限访问文件，安保人员正在前往你的位置，请勿试图抵抗。");
}
}

</script>

</head>

<body>
<form><input type=button id="aa" value="存取Area-CN-05详细资料" onClick="LogIn()"></form>
</body>
