[[include :scp-wiki-cn:theme:bedrock]]
[[module CSS]]
/*
https://pixabay.com/zh/photos/chain-pearls-jewellery-necklace-784422/
https://pixabay.com/zh/photos/pocket-watch-watch-timepiece-clock-560937/
*/

:root {
    --basalt-undertone: 127, 127, 127;
    --basalt-bright-element-color: 100, 100, 100;
    --basalt-dark-element-color: 80, 80, 80;

    --main-content-top-margin: 0;
    --main-content-width: 100%;
    --main-content-side-margin: 0;
}

div#header,
div#side-bar {
    display: none;
}

div#container-wrap,
div#content-wrap,
div#main-content,
div#page-content {
    min-height: unset;
}

div#page-title {
    display: none;
}

div#page-content > p {
    margin: 0;
}

body {
    /* overflow-y: hidden; */
}

div#page-content {
    height: 100vh;
    overflow-y: auto;
    scroll-snap-type: y mandatory;
}

div#page-info-break,
div#license-area,
div#page-info,
div.page-watch-options {
    display: none;
    visibility: hidden;
}

div#page-options-container {
    display: flex;
    flex-wrap: nowrap;
    position: fixed;
    bottom: 1rem;
    left: 1rem;
    margin: 0;
    overflow: hidden;
    width: calc(100vw - 2rem);
    height: auto;
    max-width: 2rem;
    max-height: 2rem;
    border-radius: 0.5rem;
    /*box-shadow: 0 0 0.5rem rgba(0, 0, 0, 0.25);*/
    background-color: rgb(var(--basalt-background-color));
    white-space: nowrap;
    transition: ease 0.25s;
}

div#page-options-container::before,
div#page-options-container::after {
    content: "";
    width: 2rem;
    height: 2rem;
    flex-shrink: 0;
    background-image: url("https://ah-sandbox.wikidot.com/local--files/collab:ared-hub/pencil.svg");
    background-position: left center;
    background-size: 2rem 2rem;
    background-repeat: no-repeat;
}

div[id*="page-options-bottom"] {
    flex-wrap: nowrap;
}

div#page-options-container:hover {
    z-index: 10;
    overflow-x: auto;
    max-width: calc(100vw - 2rem);
    max-height: 100vh;
}

div.page-tags,
div.page-tags span {
    display: none !important;
}

div#footer {
    padding: 0;
    position: fixed;
    bottom: 1rem;
    right: 1rem;
    margin: 0;
    overflow: hidden;
    max-width: 2rem;
    max-height: 2rem;
    border-radius: 0.5rem;
    /*box-shadow: 0 0 0.5rem rgba(0, 0, 0, 0.25);*/
    background-color: rgb(var(--basalt-background-color));
    white-space: nowrap;
    transition: ease 0.25s;
    grid-template-columns: 2rem 1fr max-content max-content 2rem;
}

div#footer::before,
div#footer::after {
    content: "";
    display: block;
    width: 2rem;
    height: 2rem;
    background-image: url("https://ah-sandbox.wikidot.com/local--files/collab:ared-hub/footer.svg");
    background-position: left center;
    background-size: 2rem 2rem;
    background-repeat: no-repeat;
}

div#footer div.options {
    flex-wrap: nowrap;
}

div#footer:hover {
    z-index: 10;
    overflow-x: auto;
    max-width: calc(100vw - 2rem);
    max-height: 100vh;
}

div#page-content > div.page-rate-widget-box {
    position: fixed;
    top: 1rem;
    right: 1rem;
    margin: 0;
    border-radius: 0.5rem;
}

div.table {
    overflow: auto;
    margin: 0 auto;
    max-width: 70vw !important;
    max-height: 40vh;
}

div.table table.wiki-content-table {
    margin: 0 auto;
}

div.page-wrap {
    height: 100vh;
    width: 100%;
    display: grid;
    scroll-snap-align: start;
}

div.page-wrap > div {
     font-size: max(var(--true-font-size), 1.125vw);
}

.yui-navset.yui-navset-top .yui-content {
    border: none;
    padding: .625rem 0 0 0;
}

/* PAGE STYLE */

div.page-wrap.main-title {
    background-image: linear-gradient(36deg, transparent 0%, rgba(0, 0, 0, 0.25) 35%, rgba(0, 0, 0, 0.1) 35%, transparent);
}

div.page-wrap.main-title > div {
    align-self: center;
}

div.page-wrap.main-title > div img {
    width: max(8rem, 25vw);
    margin: 0 calc(50% - max(8rem, 25vw) / 2);
    opacity: 0.6;
    transition: ease 0.25s;
}

div.page-wrap.main-title > div img:hover {
    opacity: 1;
}

div.page-wrap.main-title > div h1 {
    font-size: max(2rem, 5vw);
    font-weight: 900;
    letter-spacing: max(0.4rem, 1vw);
    text-align: center;
}

div.page-wrap.dia {
    grid-template-areas: "box .";
    grid-template-columns: 1fr 1fr;
    background-size: cover;
    background-repeat: no-repeat;
    background-position: center center;
    background-attachment: fixed;
}

div.page-wrap.dia.right {
    grid-template-areas: ". box";
}

div.page-wrap.dia > div {
    grid-area: box;
    padding: max(4vw, 1.5rem);
    background-color: rgba(255, 255, 255, 0.95);
    overflow-y: auto;
}

@media (max-width: 800px) {
div.page-wrap.dia {
    grid-template-columns: 3fr 2fr;
}

div.page-wrap.dia.right {
    grid-template-columns: 2fr 3fr;
}
}

@media (max-width: 600px) {
div.page-wrap.dia {
    grid-template-columns: 5fr 3fr;
}

div.page-wrap.dia.right {
    grid-template-columns: 3fr 5fr;
}
}

@media (max-width: 400px) {
div.page-wrap.dia {
    grid-template-columns: 1fr 0;
}

div.page-wrap.dia.right {
    grid-template-columns: 0 1fr;
}

div.page-wrap.dia > div {
    margin: 1rem;
    border-radius: 0.5rem;
}
}

div.page-wrap.about {
    background-image: url("https://ah-sandbox.wikidot.com/local--files/collab:ared-hub/bg1.jpg");
}

div.page-wrap.description {
    background-image: url("https://ah-sandbox.wikidot.com/local--files/collab:ared-hub/bg2.jpg");
}
