[[module CSS]]
 @import url(https://fonts.googleapis.com/css2?family=Montserrat:wght@700;800&display=swap);

 :root {
    --title-font: "Montserrat", sans-serif;
}

 #page-title {
    display: none;
}

 .meta-title {
    margin: 0 0 .8em;

    text-align: center;
}

 @media only screen and (min-width: 56.25rem) {
     .meta-title {
