[[module CSS]]
 :root{
     --accentColor: #D22B2B;
     --accentColorLite: #AD2323;
     --hue: hue;

     --bgColor: #191b1e;
     --fgColor: #0e0f11;

     --lgurl: url(https://scp-wiki.wdfiles.com/local--files/armed-with-hell-flames-and-fury/chaos-insurgency-sigil-1.png);

     --header-title: "CHAOS INSURGENCY";
     --header-subtitle: "CREATING LOGIC OUT OF ILLOGIC";
     --titleColor: title_colour;
     --subtitleColor: subtitle_colour;
}

#extra-div-1 {
     filter: invert(0) opacity(.15);
}

#header h1 a::before {
     background: -webkit-linear-gradient(270deg, rgba(255,255,255,1), rgba(179,179,179,1) 75%, rgba(47, 51, 60,1) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
}

 .zm,
 .zm hr,
 .zm.comment {
   --accentColor: hsl(220, 18%, 47%);
   --accentColorLite: hsla(220, 18%, 47%, .15);
}

 .zm .fnnum::after,
 .zm .fnnum:hover,
 .zm .fncon:before {
   --fnColor: var(--accentColor);
}

 .zm.comment {
     display: block;
     margin: .8rem;
     padding: .02rem .8rem;

     color: var(--textColor);
     background: var(--accentColorLite);
}

.report-box hr {
    border-top: 3px solid var(--accentColor);
}

.monologue td {
    text-align: center;
    padding: 0.8rem;
    border: 1px solid var(--accentColor);
    line-height: 1.5;
}

.monologue th {
    text-align: center;
    padding: 0.3rem;
    background: var(--accentColor);
    border: 1px solid var(--accentColor);
}

.monologue th h3 {
    color: var(--textColor);
    text-decoration-color: var(--textColor);
}

.monologue td .background-text {
     opacity: .8;
     font-style: italic;
}

.comment {
     text-align: justify;
}

 #u-fade-away {
 /* Mouse Hover Reveals Rating Module */
    opacity: calc(1 / 3);
    transition: all .25s ease-in-out;
}

 #u-fade-away:hover {
    opacity: 1;
}

.argus {
    --accentColor: #879DCC;
    --accentColorLite: #AEC0E6;
    --fnColor: var(--accentColor);
    background: #eff4ff;
    border: thick solid #5B5D61;
    color: #333;
    text-align: center;
    margin-bottom: 2.5rem;
    margin-top: 2.5rem;
    font: 110% var(--header-font);
    font-weight: bold;
}

.argus hr {
    border: thin solid #5B5D61;
}

tt {
    background-color: transparent;
    padding: 0em;
}
[[/module]]

