[[include :scp-wiki:theme:y2k]]
[[module css]]

/* ------------------------------------------------------------------- */
/* --------------------- Header by Ignota, --------------------- */
/* ------------------ as seen on SCP-8500 ------------------ */
/* -------- https://scp-wiki.wikidot.com/scp-8500 -------- */
/* ------------------------------------------------------------------- */

div#header
{
  background-image: url('https://scp-wiki.wdfiles.com/local--files/scp-8500/y2klogo.png') !important;
  image-rendering: pixelated;
}
div#container-wrap
{
  background-image: url('https://scp-wiki.wdfiles.com/local--files/scp-8500/header.png') !important;
}

/* ------------------------------------------------------------------- */
/* ------------------------------------------------------------------- */
/* ------------------------------------------------------------------- */

a {
    color: rgb(0, 132, 132);
}

#top-bar a,
#top-bar ul li.sfhover a,
#top-bar ul li:hover a,
#side-bar a,
#interwiki a {
    color: rgb(0, 132, 132);
}

#side-bar a:visited,
#interwiki a:visited,
#login-status ul a,
#login-status ul a:hover {
    color: #800080;
}

div.yui-navset ul.yui-nav .selected a,
div.yui-navset ul.yui-nav .selected a:focus,
div.yui-navset ul.yui-nav .selected a:hover {
    background: #639cff;
}

.yui-navset .yui-nav,
.yui-navset .yui-navset-top .yui-nav {
    border-color: #5b6eff;
}

.yui-content {
    margin-bottom: 0.5em;
}

.yui-content .collapsible-block-folded{
    margin: 40px;
    padding: 0.75em;
    border: 0.09rem dashed #666666;
    text-align:center;
}

.yui-content .collapsible-block-unfolded-link{
    margin: 40px;
    padding: 0.75em;
    border: 0.09rem dashed #666666;
    text-align:center;
}

@media only screen and (max-width: 767.98px) {
.yui-content .collapsible-block-folded{
    margin: 0.4em;
    padding: 0.75em;
}

.yui-content .collapsible-block-unfolded-link{
    margin: 0.4em;
    padding: 0.75em;
}
[[/module]]

