[[module css]]
/*
    Tema Blackberry
    [Tema Sigma-9 de 2022]
    [Hecho en la Rama Hispana (Ñ)]
    Diseñado por Belasias y retocado por Dc_Yerko
*/

/* CUERPO */

body {
    background-color: #131313;
    font-size: 0.80em;
    color: #808080;
}

/* ENLACES */

a:visited {
    color: #ba2f3a;
}

/* FONDO DE CABECERA */

div#container-wrap {
    background: url(http://lafundacionscp.wdfiles.com/local--files/theme%3Ablackberry/body_black.png) top left repeat-x;
}

/* BARRA SUPERIOR */

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: #212121;
    color: #bb0011;
    border-left: solid 1px #808080;
    border-right: solid 1px #808080;
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-top: 1px solid #666666;
}

#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: #404040;
    color: #bb0011;
}

/* BARRA LATERAL */

#side-bar .side-block {
    padding: 10px;
    border: 1px solid #710000;
    border-radius: 10px;
    box-shadow: 0 2px 6px rgba(113,0,0,.5);
    background: #212121;
    margin-bottom: 15px;
}

#side-bar .heading {
    color: #808080;
}

#side-bar .side-block.media {
    padding: 10px;
    border: 1px solid #710000;
    border-radius: 10px;
    box-shadow: 0 2px 6px rgba(113,0,0,.5);
    background: #212121;
    margin-bottom: 15px;
}

#side-bar .side-block.media img {
    filter: brightness(75%);
}

#side-bar .side-block.nuevos {
    padding: 10px;
    border: 1px solid #710000;
    border-radius: 10px;
    box-shadow: 0 2px 6px rgba(113,0,0,.5);
    background: #212121;
    margin-bottom: 15px;
}

#side-bar .side-block.guias {
    padding: 10px;
    border: 1px solid #710000;
    border-radius: 10px;
    box-shadow: 0 2px 6px rgba(113,0,0,.5);
    background: #212121;
    margin-bottom: 15px;
}

#side-bar .side-block.borradores {
    padding: 10px;
    border: 1px solid #710000;
    border-radius: 10px;
    box-shadow: 0 2px 6px rgba(113,0,0,.5);
    background: #212121;
    margin-bottom: 15px;
}

#side-bar .side-block.cc {
    border: 1px solid #710000;
    border-radius: 10px;
    box-shadow: 0 2px 6px rgba(113,0,0,.5);
    background: #212121;
    margin-bottom: 15px;
}

#side-bar .side-block.cc img {
    filter: brightness(75%);
}

#side-bar .side-block.recurso {
    padding: 10px;
    border: 1px solid #710000;
    border-radius: 10px;
    box-shadow: 0 2px 6px rgba(113,0,0,.5);
    background: #212121;
    margin-bottom: 15px;
}

/* BOTÓN DE LA BARRA LATERAL */

@media (max-width: 767px) {
    #side-bar {
        background: rgba(0,0,0, 0.5);
    }

.open-menu a {
    border: 0.2em solid #990011 !important;
    background-color: #212121 !important;
    color: #990011 !important;
    }
}

.open-menu a,
#top-bar .open-menu a {
    border: 0.2em solid #990011 !important;
    background-color: #212121 !important;
    color: #990011 !important;
}

.open-menu a:hover,
#top-bar .open-menu a:hover {
    text-decoration: none !important;
    -webkit-box-shadow: 0px 0px 20px 3px rgba(150,0,0,.7);
    -moz-box-shadow: 0px 0px 20px 3px rgba(150,0,0,.7);
    -ms-box-shadow: 0px 0px 20px 3px rgba(150,0,0,.7);
    -o-box-shadow: 0px 0px 20px 3px rgba(150,0,0,.7);
    box-shadow: 0px 0px 20px 3px rgba(150,0,0,.7);
}

/* INTERWIKI */

#interwiki .side-block {
    border: 1px solid #710000;
    border-radius: 10px;
    box-shadow: 0 2px 6px rgba(113,0,0,.5);
    background: #212121;
    margin-bottom: 15px;
}

#interwiki .heading {
    color: #808080;
}

/* PUNTUACIÓN */

.page-rate-widget-box {
    margin-right: 0px;
}

.creditRate {
    margin-right: 0px!important;
}

.page-rate-widget-box .rate-points,
.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown,
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a,
.page-rate-widget-box .cancel a {
    background-color: transparent !important;
    border: 0;
    text-transform: capitalize;
    font-weight: bold;
    color: #808080 !important;
}
.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    margin-right: 0px;
    margin-left: 0px;
}
div.page-rate-widget-box {
    background-color: #212121 !important;
    border: solid #808080 1px!important;
}
div.page-rate-widget-box span.rate-points,
div.page-rate-widget-box span.cancel {
    background-color: #212121 !important;
    border: 1px #808080 !important;
}
div.page-rate-widget-box span.rateup,
div.page-rate-widget-box span.ratedown {
    background-color: #FFF !important;
    margin-right: 0px;
    margin-left: 0px;
    color: #000!important;
}
div.page-rate-widget-box span.rateup a,
div.page-rate-widget-box span.ratedown a {
    background: #2b2b2b !important;
    color: #808080 !important;
}
div.page-rate-widget-box span.rateup a:hover {
    background: #808080 !important;
    color: #212121 !important;
}
div.page-rate-widget-box span.ratedown a:hover {
    background: #808080!important;
    color: #212121 !important;
}
div.page-rate-widget-box span.cancel a {
    color: #808080 !important;
}
div.page-rate-widget-box span.cancel a:hover {
    background: #808080!important;
    color: #212121 !important;
}

/* PUNTUACIÓN CON INFO */

.rate-box-with-credit-button > div.page-rate-widget-box {
     border-width: 0px!important;
}
#page-content .rate-box-with-credit-button {
    background-color: #212121;
    border: solid 1px #808080;
    overflow: hidden;
}
#page-content .rate-box-with-credit-button .creditButton p a {
    border-left-color: transparent;
}
#page-content .rate-box-with-credit-button .page-rate-widget-box .rate-points {
    border-left: 0;
}
#page-content .rate-box-with-credit-button .fa-info {
     color: #808080;
     line-height: 15px;
     padding-top: 1px;
}
@media (max-width: 450px) {
#page-content .rate-box-with-credit-button .fa-info {
        line-height: 14px;
    }
}
@media not all and (min-resolution:.001dpcm)
{ @supports (-webkit-appearance:none) and (stroke-color:transparent) {
    #page-content .rate-box-with-credit-button .fa-info {
        line-height: 14px;
        padding-top: 2px;
    }
}}
#page-content .rate-box-with-credit-button .fa-info:hover {
    background: #808080!important;
    color: #212121 !important;
}
#page-content .creditButtonStandalone p a {
    background-color: #212121;
    border: solid 1px #660000;
    box-shadow: none;
    color: #000000;
}
#page-content .creditButtonStandalone p a:hover {
    color: #ff0000;
}

#page-content .modalbox {
     background: #f4f4f4 !important;
     color: #000000;
     box-shadow: none;
}
.close-credits,
.credit-back {
     filter: hue-rotate(240deg);
}

/* BLOCKQUOTE */

blockquote,
div.blockquote {
    color: #999999;
    border: 1px dashed #990011;
    background-color: #212121;
}

/* CODE */

.code {
    color: #999999;
    background-color: #212121;
}

.hl-identifier {
    color: #808080;
}

.hl-code {
    color: #808080;
}

.hl-brackets {
    color: #808080;
}

.hl-reserved {
    color: #ba2f3a;
}

.hl-special {
    color: #b01;
}

.hl-string {
    color: #d61;
}

.hl-number {
    color: #2e40d1;
}

/* IMAGE BLOCK */

.scp-image-block .scp-image-caption {
    border: #999999;
    background-color: #212121;
}

.scp-image-block .scp-image-caption > p {
    background-color: #212121;
    color: #999999;
    border: #999999;
}

/* TABLAS */

table.wiki-content-table th {
    background-color: #212121;
    color: #999999;
    border: solid 1px #999999;
}

table.wiki-content-table td {
    border: solid 1px #999999;
}

/* TABS */

.yui-navset .yui-content{
    color: #999999;
    background-color: #212121;
}

.yui-navset .yui-nav a,
.yui-navset .yui-navset-top .yui-nav a {
    background-image: url("http://lafundacionscp.wdfiles.com/local--files/theme%3Ablackberry/yuitabs.png");
    color: #999999;
}

.yui-navset .yui-nav .selected a,
.yui-navset .yui-nav .selected a:focus,
.yui-navset .yui-nav .selected a:hover {
    background: url("http://lafundacionscp.wdfiles.com/local--files/theme%3Ablackberry/yuitabs.png") repeat-x left -1400px;
    color: #bfbfbf;
}
.yui-navset .yui-nav a:hover,
.yui-navset .yui-nav a:focus {
    background: #00FF00 url("http://lafundacionscp.wdfiles.com/local--files/theme%3Ablackberry/yuitabs.png") repeat-x left -1300px;
    color: #999999;
}

/* FOOTNOTES */

.hovertip {
  background-color: #212121 !important;
  border: 1px solid #b6b6b6 !important;
}

/* OWINDOW */

.owindow {
    background-color: #212121;
}

.owindow .title {
    background-color: #131313;
    color: #808080;
}

.owindow img {
    background-color: #212121 !important;
}

.owindow .button-bar a {
    background-color: #212121;
}

.owindow .button-bar a:hover {
    background-color: #3e3e3e;
}

.owindow.owait .content{
    padding-bottom: 2rem;
    background-image: url("http://lafundacionscp.wdfiles.com/local--files/theme%3Ablackberry/alt_loader.gif");
    filter: invert(.4) sepia(.5) saturate(1000%) hue-rotate(var(--tint));
}

/* CHECKBOX */

.checkbox label input {
     filter: invert(1) grayscale(1);
}

 input.checkbox, .page-history input {
     filter: invert(1) grayscale(1);
     cursor: pointer;
}

/* SELECCIÓN CON MOUSE */

::selection {
    background: #901;
    color: #ffffff;
}

/* SCROLLBAR */

::-webkit-scrollbar-thumb {
    background: #373737;
}
::-webkit-scrollbar-track {
    background: #282828;
}

/* EDITOR DE TEXTO */

 input.text,
 input.text:focus,
 input.text:hover{
     color: #808080;
     border-color: #808080;
     background-color: #212121;
}

.wd-editor-toolbar-panel div {
     filter: hue-rotate(62deg) invert(83%);
}

 textarea {
     color: #808080;
     border-color: #808080;
     background-color: #212121;
}

.change-textarea-size a{
     color: #808080;
     border-color: #808080;
     background-color: #212121;
}

 div#lock-info{
     background-color: #212121;
}

 div.buttons input, input.button, button, file, a.button {
     color: #808080;
     border-color: #808080;
     background-color: #212121;
}

/* ETIQUETAS */

#main-content .page-tags a {
    color: #b01;
}

/* FOOTER */

#footer {
    background-color: #212121;
    color: #808080;
}
[[/module]]
