[[module CSS]]

.pictureBox{
width: 95%;
padding: 10px;
border: solid 2px var(--accentColor);
border-radius: 15px;
background-color: #6B537380;
}

.grid-container {
    display: grid;
    grid-template-columns: 1fr 1fr;
    grid-gap: 20px;
}



.censor{
filter: blur(30px);
transition: 0.5s ease;
}

.censor:hover{
filter: blur(0px);
transition: 0.5s ease;
}


image{
width:90%;
}
