@import url('https://fonts.googleapis.com/css2?family=Cardo&display=swap');
@import url('https://fonts.googleapis.com/css2?family=Tangerine:wght@700&display=swap');

.container {
  position: relative;
  text-align: center;
  color: white;
}

.top-left {
  position: absolute;
  top: -5px;
  left: 16px;
font-size: 1.5vw;
font-family: mono;
}

.bottom-right {
  position: absolute;
  bottom: -5px;
  right: 16px;
font-size: 1.5vw;
font-family: mono;
}

.censor{
filter: blur(30px);
transition: 0.5s ease;
}

.censor:hover{
filter: blur(0px);
transition: 0.5s ease;
}

.nacre{
border-radius: 10px;
  background-repeat: repeat;
  background-position: top right;
background-image: url(https://i.imgur.com/36tm5UD.png);
border:solid 2px #D8C7BC;
 background-size: 300px 300px;
padding:15px;
margin-bottom: 15px;
font-family: Cardo, serif;
font-size: 16px;
}

img{
margin-bottom: 10px;
margin-top: 10px;
}

.nacre img{
border-radius: 10px;
border:solid 2px #D8C7BC;
}

.nacre a{
color: #FF1948;
background-color: #5B214Add; 
}

.nacre div a{
color: #FF1948;
background-color: #5B214Add; 
}

.nacre a.collapsible-block-link{
padding: 5px;
border-radius: 15px;
border: 2px solid #D8C7BC;
font-size: 20px;
}

