[[module CSS]]
div#extra-div-1 {
    height: 140px;
    width: 100%;
    top: 0;
    position: absolute;
    background-image: url("https://scp-sandbox-3.wdfiles.com/local--files/lego-anky/387-header-5.svg");
    background-size: cover;
}
#header {
    background: url("https://scp-sandbox-3.wdfiles.com/local--files/anky-remix/SCP_Foundation_%28emblem%29%20%281%29.svg") 0px 30px no-repeat;
    background-size: 6.5rem;
}

body {
    font-family: Arial;
    font-size: 16px;
    color: #000;
}

blockquote,
div.blockquote {
    padding: 1em;
    margin: 1em 0;
    border: 2px solid #000;
    border-radius: 10px;
    background-color: #D6EFFF;
}

.part blockquote,
.blockquote.part {
    background-color: #B8D7F3;
    border: 1px solid #000;
    border-radius: 2.5px;
}

.white blockquote,
.blockquote.white{
    background-color: #FFFFFF;
    border: 1px solid #000;
    border-radius: 2.5px;
}

.scp-image-block {
    background-color: #B8D7F3;
    border: 1px solid #000;
    border-radius: 5px;
    margin: 10px;
    padding: 0em;
}

.scp-image-block .scp-image-caption {
    background-color: #FFFFFF;
    border: 1px solid #000;
    border-radius: 5px;
    margin: 2px;
    padding: 0em;
}

[[/module]]
