[[include :scp-wiki-cn:theme:basalt darkmode=a|hidetitle=a]]

[[module css]]
body {
    background-color: var(--accentColor);
    background-image: url("http://scpdsandbox.wdfiles.com/local--files/collab%3Aclaw/Sky1.jpg");
    background-attachment: fixed;
    background-size: cover;
    background-blend-mode: luminosity;
}

body:before {
  content: ' ';
  position: fixed;
  display: block;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  background-image: linear-gradient(165deg, transparent, #000000 60%, #000000 60%, #000000 120%);
  z-index: -1;
}

.table2 {
    --accentColor: rgb(105,155,196);
}

.bigtext {
color: #699bc4;
}

@media (max-width: 1290px) {
    body {
        background-size: 100% 66%;
    }
}

#extra-div-1 {
background: linear-gradient( 180deg, rgb(0, 0, 0) 0%, var(--accentColor)100%);
box-shadow: 0px 0px 8px 4px rgb(0 0 0);
}

:root {
    --logo: url(https://scp-wiki.wdfiles.com/local--files/scp-6269/Hlogo4.png);
    --title: "采 集：";
    --subtitle: "多功能爪机部";
    --accentColor: hsl(207,43.5%,59%);
    --fnColor: #699bc4;
    --four-color: 194, 85, 0 !important;
}
[[/module]]



