[[module CSS]]
@media screen and (prefers-reduced-motion: no-preference) {

    #page-title,
    #breadcrumbs,
    #page-content>* :not(#u-sb-button), not(#side-bar .collapsible-block-link) {
     animation-name: fadeIn;
     animation-duration: 1s;
     animation-iteration-count: 1;
     animation-timing-function: ease-out;
     animation-fill-mode: backwards;
    }

    #u-sb-button {
     animation: sideBarBtnFadeIn 1s ease-out 0.1s 1 normal backwards running;
     z-index: 20;
    }
}

    #side-bar .collapsible-block-link {
     animation: CollapsibleFadeln 1s ease-out 0.1s 1 normal backwards running;
     z-index: 20;
    }
}

#page-title {
    animation-delay: 0s;
}

@keyframes sideBarBtnFadeIn {
    from {
     transform: translate(-140px, 0);
     opacity: 0;
    }

@keyframes CollapsibleFadeln {
    from {
     transform: translate(-140px, 0);
     opacity: 0;
    }

@keyframes fadeIn {
    from {
     opacity: 0;
     transform: translate(0, 30px);
    }

    to {
     opacity: 1;
     transform: translate(0, 0);
    }
}

:root {
    --fade-in-delay: 0s;
}

#page-content> :nth-child(1) {
    animation-delay: calc(1 * var(--fade-in-delay));
}

#page-content> :nth-child(2) :not(#u-sb-button) {
    animation-delay: calc(2 * var(--fade-in-delay));
}

#page-content> :nth-child(3) {
    animation-delay: calc(3 * var(--fade-in-delay));
}

#page-content> :nth-child(4) {
    animation-delay: calc(4 * var(--fade-in-delay));
}

