[[module CSS]]
.anchor {
    position: sticky;
    height:0;
    top: 0;
}
.sidebox {
    background-color: #FEFEFE;
    box-shadow: 0px 0px 5.2px rgba(0,0,0,0.32);
    padding: .14rem;
    margin-top: 0.5rem;
    margin-bottom: 8px;
    padding: 6px;
    width: calc((87vw - 870px)/2);
    max-height: calc(100vh - 18rem);
    position: absolute;
    top: 0;
    left: calc(104% + 13px - 1vw);
    z-index: 5;
    overflow: auto;
}
@media (max-width: 1290px) {
    .sidebox {
        display: none;
        visibility:hidden;

    }
}

:root {
    --title: "SCP基金会";
